import React, { useState, useEffect } from 'react';

const Projects = () => {
  const [projects, setProjects] = useState({});
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('./data/projects.json')
      .then(response => response.json())
      .then(data => setProjects(data))
      .catch(error => setError(error.message));
  }, []);

  if (error) return <div className="text-red-600">{error}</div>;

  const projectArray = Object.entries(projects).map(([name, data]) => ({
    name,
    ...data
  }));

  return (
    <div className="space-y-4 book-1-accordion p-2">
      {projectArray.map((project, index) => (
        <div key={index} className="py-2">
          <h3 className="font-bold">{project.name}</h3>
          <div className="space-x-2">
            {project.type?.map((type, typeIndex) => (
              <span key={typeIndex} className="border-b">{type}</span>
            ))}
          </div>
          <p>{project.description}</p>
          <p>Year: {project.year} | Authors: {project.authors}</p>
          <a href={project.link} className="border">Learn More</a>
          <div className="w-full h-1 bg-black"></div>
        </div>
      ))}
    </div>
  );
};

export default Projects;
