import React from 'react';
import Bookshelf from './BookShelf';

const PersonalProfile = () => {
    const tags = ['digital humanities', 'semantic web', 'information science', 'knowledge engineering', 'GLAM'];

  return (
    <div className="min-h-screen flex items-center justify-center p-4 sm:p-6 md:p-8">
      <div className="w-full max-w-6xl h-auto md:h-[70vh] flex flex-col">
        {/* Main Content Wrapper */}
        <div className="flex flex-col md:flex-row flex-1">
          {/* Left Column - Personal Info */}
          <div className="w-full md:w-2/5 flex items-center justify-center relative mb-8 md:mb-0">
      <div className="w-full">
        {/* Introduction */}
        <h2 className="text-base md:text-lg mb-2">
          Hi, I am
        </h2>
        
        {/* Name - stretched to full width */}
        <h1 className="text-5xl w-full tracking-wider">
          VALENTINA
        </h1>
        <h1 className="text-5xl font-bold w-full tracking-wider">
          PASQUAL
        </h1>
        
        {/* Title - stretched but slightly less */}
        <h2 className="text-3xl md:text-4xl lg:text-5xl mb-6 font-semibold w-full tracking-wider text-end">
          DIGITAL HUMANIST
        </h2>
        
        {/* Description */}
        <p className="mb-6 text-sm text-justify leading-relaxed">
          PhD student at University of Bologna, mainly interested in Semantic Web technologies (LOD) applied to GLAM domain.
        </p>
        
        {/* Tags */}
        <div className="flex flex-wrap gap-2 justify-start">
          {tags.map((tag) => (
            <span 
              key={tag}
              className="text-xs font-medium px-2.5 py-0.5 rounded bg-gray-200 text-gray-800"
            >
              {tag}
            </span>
          ))}
        </div>
      </div>
    </div>

          {/* Right Column - Bookshelf */}
          <div className="w-full md:w-3/5 flex flex-col">
            <div className="flex-1 hidden md:block"></div>
            <Bookshelf />
          </div>
        </div>

        {/* Decorative Lines Container */}
        <div className="w-full h-1 bg-black mt-6 md:mt-0"></div>
      </div>
    </div>
  );
};

export default PersonalProfile;
