import React, { useState, useRef, useEffect } from 'react';
import Projects from './Projects';
import Publications from './Publications';
import Curriculum from './Curriculum';

const RealisticBookShelf = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [firstBookRotated, setFirstBookRotated] = useState(true);
  const containerRef = useRef(null);
  const [leftOffset, setLeftOffset] = useState(0);
  const [bookPositions, setBookPositions] = useState([]);

  const books = [
    { 
      id: 1, 
      title: "Projects", 
      Component: "Projects",
      year: 2023,
      description: "A selection of project I worked",
      pages: 180,
      content: "A celebration of contemporary graphic design and festival aesthetics in modern culture.",
      accentColor: "color-combo-green",
      bookClass: "book-1",
      font: "poppins",
      rotationAngle: 12,
      horizontalShift: -1, // shift to the left to precisely touch the second book
      contactPoint: { x: 12, y: '40%' }, // where the book touches the second book
      icon: "&#9737;"
    },
    { 
      id: 2, 
      title: "Curriculum", 
      Component: "Projects",
      author: "John Doe",
      year: 2024,
      description: "A comprehensive exploration of design fundamentals and their practical applications in modern contexts.",
      tags: ["Design", "Education", "Theory"],
      pages: 245,
      content: "Exploring the fundamental elements of design through geometric principles and visual harmony.",
      accentColor: "color-combo-yellow",
      bookClass: "book-2",
      font:"westgate",
      rotationAngle: 0,
      isSupport: true, // indicates this book supports the first one
    },
    { 
      id: 3, 
      title: "Publications", 
      Component: "Projects",
      author: "Robert Johnson",
      year: 2024,
      description: "An anthology of modern publishing works examining the intersection of traditional and digital mediums.",
      tags: ["Publishing", "Media", "Digital"],
      pages: 320,
      content: "The art of pencil and charcoal, examining traditional drawing techniques in the digital age.",
      accentColor: "color-combo-red",
      bookClass: "book-3",
      font:"boy",
      rotationAngle: 0
    },
    { 
      id: 4, 
      title: "Contacts", 
      Component: "Projects",
      author: "Sarah Williams",
      year: 2023,
      description: "A comprehensive directory of industry professionals and creative practitioners, featuring insights and connections.",
      tags: ["Network", "Industry", "Contacts"],
      pages: 150,
      content: "Contemporary interpretations of traditional folk art patterns and methodologies.",
      accentColor: "color-combo-blue",
      font:"poppins",
      bookClass: "book-4",
      rotationAngle: 0
    }
  ];

  // Calculate book width for positioning
  const bookWidth = 12; // w-12 class equals 3rem or 48px
  
  // Control first book rotation and positioning based on any book being selected
  useEffect(() => {
    if (selectedBook) {
      setFirstBookRotated(false);
      setBookPositions([]);
    } else {
      setTimeout(() => {
        setFirstBookRotated(true);
        
        // Calculate exact contact position using trigonometry
        // When book 1 rotates by X degrees, we need to calculate where it intersects book 2
        const book1 = books[0];
        const rotationRadians = (book1.rotationAngle * Math.PI) / 180;
        
        // Calculate how much space the rotated book needs horizontally
        // sin(rotation) * height determines horizontal shift needed
        const bookHeight = 96; // Approximate book height in viewport height units (96vh)
        const rotatedWidth = Math.sin(rotationRadians) * bookHeight;
        
        // Calculate positions for books to create leaning effect
        const positions = books.map((book, index) => {
          if (index === 0) {
            // First book leans precisely against the second book
            return {
              rotationAngle: book.rotationAngle,
              horizontalShift: book.horizontalShift || 0,
              verticalShift: 0,
              width: bookWidth,
              height: bookHeight,
              contactPoint: true
            };
          }
          // Other books remain upright
          return {
            rotationAngle: 0,
            horizontalShift: index === 1 ? rotatedWidth - bookWidth : 0, // Adjust second book position
            verticalShift: 0,
            contactPoint: index === 1 // Second book is contact point
          };
        });
        setBookPositions(positions);
      }, 300);
    }
  }, [selectedBook, books]);

  // Calculate offset to ensure books don't overflow left side and maintain base position
  useEffect(() => {
    if (selectedBook && containerRef.current) {
      const extraPadding = 16; // padding to ensure no overflow
      const baseLeftPadding = 40; // minimum left padding
      
      let minOffset = 0;
      
      if (selectedBook === 1) {
        minOffset = baseLeftPadding + extraPadding; 
      } else {
        minOffset = baseLeftPadding + ((selectedBook - 1) * 8) + extraPadding;
      }
      
      setLeftOffset(minOffset);
    } else {
      setLeftOffset(0);
    }
  }, [selectedBook, books]);

  const handleBookClick = (bookId) => {
    if (selectedBook === bookId) {
      setSelectedBook(null);
    } else {
      setSelectedBook(bookId);
    }
  };

  // Function to determine the correct z-index for each book
  const getBookZIndex = (bookId, index) => {
    if (selectedBook === bookId) {
      return 30; // Selected book always on top
    }
    
    if (selectedBook) {
      return 20 - index; // Normal stacking when a book is selected
    }
    
    // Special handling for the leaning book effect
    if (firstBookRotated) {
      if (bookId === 1) {
        return 19; // First book behind second book
      }
      if (bookId === 2) {
        return 20; // Second book in front of first
      }
    }
    
    return 20 - index; // Default stacking order
  };

  return (
    <div className="h-screen w-screen flex relative overflow-hidden">
      {/* Overlay to hide the left side content when a book is open */}
      {selectedBook && (
        <div className="absolute left-0 top-0 w-screen h-screen bg-white z-10"></div>
      )}
      
      <div className="h-screen w-full flex items-end justify-start transition-all duration-500 ease-in-out relative pl-4">
        {/* Container for all books with right offset to prevent left overflow */}
        <div 
          ref={containerRef}
          className="h-[96vh] my-2 flex transition-all duration-500 ease-in-out relative z-20"
          style={{
            transform: selectedBook ? `translateX(${leftOffset}px)` : 'translateX(0)',
            paddingLeft: '60px', // Add fixed left padding of 60px
          }}
        >
          {books.map((book, index) => (
            <div
              key={book.id}
              style={{ 
                zIndex: getBookZIndex(book.id, index),
                position: 'relative',
                // Apply calculated position from bookPositions if available
                ...(bookPositions[index] && !selectedBook ? {
                  transform: `translateX(${bookPositions[index].horizontalShift || 0}px)`,
                } : {})
              }}
              className="flex transition-all duration-500 ease-in-out"
            >
              {/* Book spine with realistic rotation and leaning effect */}
              <div
                onClick={() => handleBookClick(book.id)}
                className={`${book.bookClass} book cursor-pointer flex flex-col items-center justify-center 
                          transition-all duration-300 ease-in-out
                          ${selectedBook && selectedBook !== book.id ? 'opacity-80 hover:opacity-100' : 'opacity-100'}
                          w-12 origin-bottom relative`}
                style={{
                  // Calculate precise positioning for book 1
                  transform: book.id === 1 && firstBookRotated && !selectedBook 
                    ? `translateX(${book.horizontalShift || 0}px) rotate(${book.rotationAngle}deg)` 
                    : book.id === 2 && firstBookRotated && !selectedBook
                    ? 'translateX(0) rotate(0deg)' // Ensure book 2 is exactly positioned
                    : 'rotate(0deg)',
                  transformOrigin: book.id === 1 ? 'bottom left' : 'bottom center',
                  perspective: '800px',
                  perspectiveOrigin: 'left center',
                  // Enhance contact point with precise shadow
                  boxShadow: book.id === 2 && firstBookRotated && !selectedBook 
                    ? '-1px 0px 2px rgba(0,0,0,0.15)' 
                    : 'none',
                  // Add margin to create proper spacing
                  marginLeft: book.id > 1 ? '1px' : '0',
                  // Remove the z-index here as we're handling it in the parent div
                }}
              > 
                <span 
                  className="transform whitespace-nowrap text-lg tracking-wide transition-transform duration-300 ease-in-out -rotate-90"
                  style={{
                    transform: 'rotate(-90deg)',
                    transformOrigin: 'center center',
                    display: 'inline-block'
                  }}
                >
                  <span dangerouslySetInnerHTML={{ __html: book.icon }} />
                  {book.title}
                  <span dangerouslySetInnerHTML={{ __html: book.icon }} />
                </span>
                
                {/* Book description - horizontal text */}
                <div className="absolute bottom-0 left-0 right-0">
                  <p className="text-[6px] text-left opacity-50 truncate px-1">
                    {book.description}
                  </p>
                </div>
              </div>
              
              {/* Open book content panel */}
              <div 
                className={`overflow-hidden overflow-y-auto transition-all duration-500 ease-in-out bg-white
                          shadow-2xl rounded-l-lg relative
                          ${selectedBook === book.id ? 'w-[calc(80vw-24px)]' : 'w-0'} 
                          ${selectedBook === book.id ? 'opacity-100' : 'opacity-0'}`}
              >
                <div className="p-6 h-full">
                  <div className="flex justify-between items-center mb-6">
                    <h2 className="text-2xl font-bold">{book.title}</h2>
                    <button 
                      onClick={() => handleBookClick(book.id)}
                      className="text-gray-500 hover:text-gray-800"
                    >
                      &times; Close
                    </button>
                  </div>
                  
                  <div className="content-container">
                    {book.title === "Projects" && <Projects />}
                    {book.title === "Publications" && <Publications />}
                    {book.title === "Curriculum" && <Curriculum />}
                    {book.title === "Contacts" && <div>Contact content here</div>}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RealisticBookShelf;
