import React, { useEffect, useRef, useState, useLayoutEffect } from 'react';
import Matter from 'matter-js';
import './index.css';

const PhysicsBookshelf = () => {
    const getCSSBackgroundColor = (colorComboClass) => {
        const tempDiv = document.createElement('div');
        tempDiv.className = colorComboClass;
        document.body.appendChild(tempDiv);
        const backgroundColor = window.getComputedStyle(tempDiv).backgroundColor;
        document.body.removeChild(tempDiv);
        return backgroundColor;
    };
    
    const getCSSTextColor = (colorComboClass) => {
        const tempDiv = document.createElement('div');
        tempDiv.className = colorComboClass;
        document.body.appendChild(tempDiv);
        const textColor = window.getComputedStyle(tempDiv).color;
        document.body.removeChild(tempDiv);
        return textColor;
    };

    const getFontFamily = (fontClass) => {
        switch (fontClass) {
            case 'westgate':
                return 'westgate, sans-serif';
            case 'boy':
                return 'shuriken-std, sans-serif';
            default:
                return 'monospace';
        }
    };
    
    const getFontWeight = (fontClass) => {
        switch (fontClass) {
            case 'westgate':
                return 700;
            case 'boy':
                return 400;
            default:
                return 400;
        }
    };

    const physicsRef = useRef({
        engine: null,
        render: null,
        runner: null,
        books: {},
        initialized: false,
        lastSelectedBook: null 
    });
  
    const sceneRef = useRef(null);
    const [selectedBook, setSelectedBook] = useState(null);
    const [showContent, setShowContent] = useState(false);
    const [removedBooks, setRemovedBooks] = useState(new Set());
    const [reAddedBook, setReAddedBook] = useState(null);
  
    const books = [
        { 
            id: 4, 
            title: "Contacts", 
            width: 100,
            height: 360,
            author: "Sarah Williams",
            year: 2023,
            description: "A comprehensive directory of industry professionals and creative practitioners, featuring insights and connections.",
            tags: ["Network", "Industry", "Contacts"],
            pages: 150,
            content: "Contemporary interpretations of traditional folk art patterns and methodologies.",
            colorCombo: "color-combo-blue",
            fontClass: "westgate" 
        },
        { 
            id: 3, 
            title: "Publications", 
            width: 120,
            height: 240,
            author: "Robert Johnson",
            year: 2024,
            description: "An anthology of modern publishing works examining the intersection of traditional and digital mediums.",
            tags: ["Publishing", "Media", "Digital"],
            pages: 320,
            content: "The art of pencil and charcoal, examining traditional drawing techniques in the digital age.",
            colorCombo: "color-combo-red",
            fontClass: "boy" 
        },
        { 
            id: 2, 
            title: "Projects", 
            width: 70,
            height: 400,
            author: "Jane Smith",
            year: 2023,
            description: "A collection of innovative design projects showcasing contemporary approaches to visual communication and digital media.",
            tags: ["Portfolio", "Design", "Digital"],
            pages: 180,
            content: "A celebration of contemporary graphic design and festival aesthetics in modern culture.",
            colorCombo: "color-combo-green",
            fontClass: "westgate" 
        },
        { 
            id: 1, 
            title: "Curriculum", 
            width: 80,
            height: 320,
            author: "John Doe",
            year: 2024,
            description: "A comprehensive exploration of design fundamentals and their practical applications in modern contexts. This curriculum covers everything from basic principles to advanced techniques.",
            tags: ["Design", "Education", "Theory"],
            pages: 245,
            content: "Exploring the fundamental elements of design through geometric principles and visual harmony.",
            colorCombo: "color-combo-yellow",
            fontClass: "boy" 
        }
    ];

    const createBookSVG = (book) => {
        const fontFamily = getFontFamily(book.fontClass);
        const fontWeight = getFontWeight(book.fontClass);
        return `
            <svg xmlns="http://www.w3.org/2000/svg" width="${book.width}" height="${book.height}">
                <rect width="${book.width}" height="${book.height}" fill="${getCSSBackgroundColor(book.colorCombo)}" />
                <text 
                    x="50%" 
                    y="50%" 
                    dominant-baseline="middle" 
                    text-anchor="middle"
                    fill="${getCSSTextColor(book.colorCombo)}"
                    font-size="30"
                    font-family="${fontFamily}"
                    font-weight="${fontWeight}"
                    font-style="normal"
                    transform="rotate(90, ${book.width / 2}, ${book.height / 2})"
                >
                    ${book.title}
                </text>
            </svg>
        `;
    };

    useLayoutEffect(() => {
        if (physicsRef.current.initialized) {
            const runner = physicsRef.current.runner;
            const render = physicsRef.current.render;
            const engine = physicsRef.current.engine;
      
            if (runner) Matter.Runner.stop(runner);
            if (render) Matter.Render.stop(render);
            if (engine && engine.world) {
                Matter.World.clear(engine.world);
                Matter.Engine.clear(engine);
            }
            if (render && render.canvas) {
                render.canvas.remove();
            }
            physicsRef.current.initialized = false;
        }
    
        const Engine = Matter.Engine;
        const Render = Matter.Render;
        const Runner = Matter.Runner;
        const World = Matter.World;
        const Bodies = Matter.Bodies;

        const engine = Engine.create({
            gravity: { x: 0, y: 2 }
        });
    
        const render = Render.create({
            element: sceneRef.current,
            engine: engine,
            options: {
                width: 800,
                height: 400,
                wireframes: false,
                background: 'transparent',
                pixelRatio: window.devicePixelRatio
            }
        });

        render.canvas.addEventListener('mousedown', (event) => {
            const canvasBounds = render.canvas.getBoundingClientRect();
            const scaledX = (event.clientX - canvasBounds.left) * (render.options.width / canvasBounds.width);
            const scaledY = (event.clientY - canvasBounds.top) * (render.options.height / canvasBounds.height);
      
            const bodies = Matter.Composite.allBodies(engine.world);
            const clickedBody = bodies.find(body => 
                body.label.startsWith('book-') &&
                Matter.Bounds.contains(body.bounds, { x: scaledX, y: scaledY }) &&
                Matter.Vertices.contains(body.vertices, { x: scaledX, y: scaledY })
            );

            if (clickedBody) {
                const bookId = parseInt(clickedBody.label.split('-')[1]);
                handleBookClick(bookId);
            }
        });

        const shelf = Bodies.rectangle(0, 385, 1000, 1, { 
            isStatic: true,
            angle: Math.PI * -0.00,
            render: { fillStyle: 'transparent' }
        });
    
        const ground = Bodies.rectangle(400, 400, 810, 20, { 
            isStatic: true,
            render: { fillStyle: 'transparent' }
        });

        const leftWall = Bodies.rectangle(500, 200, 40, 500, { 
            isStatic: true,
            render: { fillStyle: 'transparent' }
        });
    
        const rightBoundary = Bodies.rectangle(0, 300, 4, 350, { 
            isStatic: true,
            render: { fillStyle: '#000000' }
        });
    
        const rightWall = Bodies.rectangle(0, 200, 20, 400, { 
            isStatic: true,
            render: { fillStyle: '#ffffff' }
        });

        World.add(engine.world, [ground, shelf, leftWall, rightWall, rightBoundary]);

        physicsRef.current.books = {};

        const availableBooks = books.filter(book => !removedBooks.has(book.id));
        availableBooks.forEach((book, index) => {
            const xPos = 100 + (index * 60);
            const xOffset = Math.random() * 30 - 15;
            const bookBody = Bodies.rectangle(xPos + xOffset, -100 - (index * 50), book.width, book.height, {
                render: {
                    fillStyle: getCSSBackgroundColor(book.colorCombo),
                    strokeStyle: getCSSTextColor(book.colorCombo),
                    lineWidth: 1,
                    sprite: {
                        texture: 'data:image/svg+xml;charset=utf-8,' + encodeURIComponent(createBookSVG(book)),
                        xScale: 1,
                        yScale: 1,
                    },
                },
                chamfer: { radius: 2 },
                friction: 0.9,
                restitution: 0.1,
                density: 0.002,
                frictionAir: 0.001,
                slop: 0.05,
                label: `book-${book.id}`,
            });
      
            physicsRef.current.books[book.id] = bookBody;
            World.add(engine.world, bookBody);
        });

        physicsRef.current.engine = engine;
        physicsRef.current.render = render;
        physicsRef.current.runner = Runner.create();
        physicsRef.current.initialized = true;

        Runner.run(physicsRef.current.runner, engine);
        Render.run(render);

        return () => {
            Runner.stop(physicsRef.current.runner);
            Render.stop(render);
            World.clear(engine.world);
            Engine.clear(engine);
            render.canvas.remove();
            physicsRef.current.initialized = false;
        };
    }, [removedBooks]);

    const handleBookClick = React.useCallback((bookId) => {
        if (!physicsRef.current.initialized) return;
    
        if (selectedBook === bookId) {
            setShowContent(false);
            setRemovedBooks(prev => {
                const newSet = new Set(prev);
                newSet.delete(selectedBook);
                return newSet;
            });
            setReAddedBook(selectedBook);
      
            setTimeout(() => {
                setSelectedBook(null);
                setTimeout(() => {
                    setReAddedBook(null);
                }, 100);
                setSelectedBook(null);
            }, 500);
            return;
        }

        const selectedBody = physicsRef.current.books[bookId];
        if (!selectedBody) return;

        const fadeOutDuration = 15;
        let step = 0;
    
        const fadeInterval = setInterval(() => {
            if (step >= fadeOutDuration) {
                clearInterval(fadeInterval);
                Matter.World.remove(physicsRef.current.engine.world, selectedBody);
                return;
            }
      
            const progress = step / fadeOutDuration;
            if (selectedBody.render) {
                selectedBody.render.opacity = 1 - progress;
            }
      
            step++;
        }, 16);
    
        setTimeout(() => {
            setSelectedBook(bookId);
            setShowContent(true);
        }, 300);
    }, [selectedBook, books, setReAddedBook]);

    return (
        <div className="w-full overflow-hidden">
            <div className="max-w-4xl mx-auto">
                <div ref={sceneRef} className="relative w-100" style={{ touchAction: 'none' }} />
        
                {/* Book labels */}
                <div className="absolute inset-0" style={{ pointerEvents: 'none' }}>
                    {books.map((book) => {
                        if (removedBooks.has(book.id)) return null;
                        const body = physicsRef.current.books[book.id];
                        if (!body) return null;
            
                        const { position, angle } = body;
                        return (
                            <div
                                key={book.id}
                                className="absolute"
                                style={{
                                    left: `${position.x}px`,
                                    top: `${position.y}px`,
                                    transform: `translate(-50%, -50%) rotate(${angle}rad)`,
                                    width: `${book.width}px`,
                                    height: `${book.height}px`,
                                }}
                            />
                        );
                    })}
                </div>

        <div className={`fixed inset-0 bg-white transition-all duration-500 ease-in-out ${showContent ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
          {selectedBook && (
            <div className="max-w-4xl mx-auto p-8 h-full flex flex-col">
              <div className="flex justify-between items-start mb-8">
                <div>
                  <h2 className="text-4xl tracking-wider font-bold mb-2">
                    {books.find(b => b.id === selectedBook)?.title}
                  </h2>
                  <div className="font-mono">
                    <p>By {books.find(b => b.id === selectedBook)?.author} ({books.find(b => b.id === selectedBook)?.year})</p>
                    <p>{books.find(b => b.id === selectedBook)?.pages} pages</p>
                  </div>
                </div>
                <button 
                  onClick={() => handleBookClick(selectedBook)}
                  className="transition-colors duration-300"
                >
                  <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              <div className="flex-1 overflow-auto">
                <div className="prose max-w-none">
                  <p className="text-lg leading-relaxed mb-6">
                    {books.find(b => b.id === selectedBook)?.description}
                  </p>
                  <div className="mb-6">
                    <h3 className="font-mono text-xl mb-2">Tags</h3>
                    <div className="flex gap-2 flex-wrap">
                      {books.find(b => b.id === selectedBook)?.tags.map(tag => (
                        <span key={tag} className="px-3 py-1 rounded-full text-sm">
                          {tag}
                        </span>
                      ))}
                    </div>
                  </div>
                  <div>
                    <h3 className="font-mono text-xl mb-2">Preview</h3>
                    <p className="font-mono">
                      {books.find(b => b.id === selectedBook)?.content}
                    </p>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PhysicsBookshelf;
