import React, { useEffect, useState } from 'react';

const CareerTimeline = () => {
  const [careerData, setCareerData] = useState({
    education: [],
    employment: []
  });
  
  useEffect(() => {
    const fetchAndParseCareerData = async () => {
      try {
        const response = await fetch('./data/career.bib').then(res => res.text());
        const entries = parseBibTeX(response);
        
        // Group entries by category
        const grouped = entries.reduce((acc, entry) => {
          const category = entry.category?.toLowerCase() || 
                          (entry.type?.toLowerCase() === 'education' ? 'education' : 'employment');
          
          if (!acc[category]) {
            acc[category] = [];
          }
          acc[category].push(entry);
          return acc;
        }, {});
        
        // Sort entries by year in descending order within each category
        if (grouped.education) {
          grouped.education.sort((a, b) => b.year - a.year);
        }
        
        if (grouped.employment) {
          grouped.employment.sort((a, b) => b.year - a.year);
        }
          
        setCareerData(grouped);
      } catch (error) {
        console.error('Error reading file:', error);
      }
    };

    fetchAndParseCareerData();
  }, []);

  const parseBibTeX = (text) => {
    const entries = [];
    const entryRegex = /@(\w+)\s*{\s*([^,]*),\s*((?:[^{}]*|{[^{}]*})*)\s*}/g;
    const fieldRegex = /(\w+)\s*=\s*{([^}]*)}/g;

    let match;
    while ((match = entryRegex.exec(text)) !== null) {
      const [, type, key, content] = match;
      const entry = { type, key };
      
      let fieldMatch;
      while ((fieldMatch = fieldRegex.exec(content)) !== null) {
        const [, field, value] = fieldMatch;
        entry[field.toLowerCase()] = value.trim();
      }
      entries.push(entry);
    }

    return entries;
  };

  const formatEducation = (edu) => {
    const degree = edu.degree || '';
    const institution = edu.institution ? `. ${edu.institution}` : '';
    const year = edu.year ? ` (${edu.year})` : '';
    const department = edu.department ? `. ${edu.department}` : '';
    const thesis = edu.thesis ? `. Thesis: "${edu.thesis}"` : '';
    const advisor = edu.advisor ? `. Advisor: ${edu.advisor}` : '';
    
    return (
      <>
        <div className="font-semibold">{degree}{year}</div>
        <div>{institution}{department}{thesis}{advisor}</div>
      </>
    );
  };

  const formatEmployment = (job) => {
    const position = job.position || '';
    const company = job.company ? ` at ${job.company}` : '';
    const startYear = job.startyear || '';
    const endYear = job.endyear || 'Present';
    const dateRange = startYear ? ` (${startYear} - ${endYear})` : '';
    const location = job.location ? `. ${job.location}` : '';
    const description = job.description ? `\n${job.description}` : '';
    
    return (
      <>
        <div className="font-semibold">{position}{company}{dateRange}</div>
        <div>{location}</div>
        {description && <p className="mt-2">{description}</p>}
      </>
    );
  };

  return (
    <div className="w-full max-w-4xl mx-auto">
      {/* Education Section */}
      <div className="mb-8">
        <div className="book-3 p-2 rounded-lg shadow-sm mb-4 border-2">
          <h2><span>Education</span></h2>
        </div>
        <div className="space-y-4 pl-4">
          {careerData.education?.map((edu, index) => (
            <div key={index} className="mb-4 border-l-2 border-rose-300 pl-4">
              <div className="text-sm">
                {formatEducation(edu)}
              </div>
              {edu.url && (
                <a 
                  href={edu.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-sm text-rose-700 hover:underline mt-1 inline-block"
                >
                  View Details
                </a>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Employment Section */}
      <div className="mb-8">
        <div className="book-3 p-2 rounded-lg shadow-sm mb-4 border-2">
          <h2><span>Employment</span></h2>
        </div>
        <div className="space-y-4 pl-4">
          {careerData.employment?.map((job, index) => (
            <div key={index} className="mb-4 border-l-2 border-rose-300 pl-4">
              <div className="text-sm">
                {formatEmployment(job)}
              </div>
              {job.url && (
                <a 
                  href={job.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-sm text-rose-700 hover:underline mt-1 inline-block"
                >
                  Company Website
                </a>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CareerTimeline;
