import React, { useEffect, useState } from 'react';

const Publications = () => {
  const [publications, setPublications] = useState([]);
  
  useEffect(() => {
    const fetchAndParseBib = async () => {
      try {
        const response = await window.fs.readFile('references.bib', { encoding: 'utf8' });
        const entries = parseBibTeX(response);
        const sortedEntries = entries.sort((a, b) => b.year - a.year);
        setPublications(sortedEntries);
      } catch (error) {
        console.error('Error reading file:', error);
      }
    };

    fetchAndParseBib();
  }, []);

  const parseBibTeX = (text) => {
    const entries = [];
    const entryRegex = /@(\w+)\s*{\s*([^,]*),\s*((?:[^{}]*|{[^{}]*})*)\s*}/g;
    const fieldRegex = /(\w+)\s*=\s*{([^}]*)}/g;

    let match;
    while ((match = entryRegex.exec(text)) !== null) {
      const [, type, key, content] = match;
      const entry = { type, key };
      
      let fieldMatch;
      while ((fieldMatch = fieldRegex.exec(content)) !== null) {
        const [, field, value] = fieldMatch;
        entry[field.toLowerCase()] = value.trim();
      }
      entries.push(entry);
    }

    return entries;
  };

  const formatAuthors = (authors) => {
    if (!authors) return '';
    return authors.split(' and ')
      .map(author => {
        if (author.includes('Pasqual, Valentina') || author.includes('Pasqual, V.')) {
          return `**${author}**`;
        }
        return author;
      })
      .join(', ');
  };

  const formatCitation = (pub) => {
    const authors = formatAuthors(pub.author);
    const year = pub.year ? `(${pub.year})` : '';
    const title = pub.title ? `. ${pub.title}` : '';
    const journal = pub.journal ? `. ${pub.journal}` : '';
    const conference = pub.booktitle ? `. In: ${pub.booktitle}` : '';
    const volume = pub.volume ? `, ${pub.volume}` : '';
    const pages = pub.pages ? `, pp. ${pub.pages}` : '';
    const doi = pub.doi ? `. DOI: ${pub.doi}` : '';

    return `${authors} ${year}${title}${journal}${conference}${volume}${pages}${doi}`;
  };

  return (
    <div className="w-full max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">Publications</h1>
      <div className="space-y-4">
        {publications.map((pub, index) => (
          <div key={index} className="mb-4">
            <p className="text-sm">{formatCitation(pub)}</p>
            {pub.doi && (
              <a 
                href={`https://doi.org/${pub.doi}`}
                target="_blank"
                rel="noopener noreferrer"
                className="text-sm text-blue-600 hover:underline"
              >
                View Publication
              </a>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Publications;
