import React from 'react';
import Bookshelf from './BookShelf';

const PersonalProfile = () => {
  return (
    <div className="h-screen flex items-center justify-center">
      <div className="w-full h-[70vh] flex flex-col">
        {/* Main Content Wrapper */}
        <div className="flex flex-1">
          {/* Left Column - Personal Info */}
          <div className="w-3/5 flex items-center justify-center relative">
            <div className="text-center max-w-md">
              <h1 className="text-4xl mb-4">
                Hi, I am VALENTINA PASQUAL. 
              </h1>
              <h1 class="text-4xl mb-4">DIGITAL HUMANIST</h1>
              <p className="mb-4">
                I am a PhD student at University of Bologna. I'm mainly interested in Semantic Web technologies (LOD) applied to GLAM domain.
              </p>
              <div className="flex flex-wrap gap-2 justify-center">
                {['digital humanities', 'semantic web', 'information science', 'knowledge engineering', 'GLAM'].map((tag) => (
                  <span 
                    key={tag} 
                    className="text-xs font-medium px-2.5 py-0.5 rounded"
                  >
                    {tag}
                  </span>
                ))}
              </div>
            </div>
          </div>

          <div className="w-2/5 flex flex-col">
            <div className="flex-1"></div>
            <Bookshelf />
          </div>
        </div>

        {/* Decorative Lines Container */}
        <div className="w-full h-1 bg-black"></div>
      </div>
    </div>
  );
};

export default PersonalProfile;
