import React, { useEffect, useRef, useState } from 'react';
import Matter from 'matter-js';

const BookShelf = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [showContent, setShowContent] = useState(false);
  const sceneRef = useRef(null);
  const engineRef = useRef(null);
  const renderRef = useRef(null);
  const booksRef = useRef({});
  
  const books = [
    { 
      id: 1, 
      title: "Curriculum", 
      width: 64,
      height: 256,
      x: 200,
      content: "Exploring the fundamental elements of design through geometric principles and visual harmony."
    },
    { 
      id: 2, 
      title: "Projects", 
      width: 56,
      height: 320,
      x: 280,
      content: "A celebration of contemporary graphic design and festival aesthetics in modern culture."
    },
    { 
      id: 3, 
      title: "Publications", 
      width: 96,
      height: 192,
      x: 380,
      content: "The art of pencil and charcoal, examining traditional drawing techniques in the digital age."
    },
    { 
      id: 4, 
      title: "Contacts", 
      width: 80,
      height: 288,
      x: 500,
      content: "Contemporary interpretations of traditional folk art patterns and methodologies."
    }
  ];

  useEffect(() => {
    const Engine = Matter.Engine;
    const Render = Matter.Render;
    const World = Matter.World;
    const Bodies = Matter.Bodies;
    const Constraint = Matter.Constraint;

    const engine = Engine.create({
      gravity: { x: 0, y: 0.5, scale: 0.001 }
    });
    engineRef.current = engine;

    const render = Render.create({
      element: sceneRef.current,
      engine: engine,
      options: {
        width: 800,
        height: 600,
        wireframes: false,
        background: 'transparent'
      }
    });
    renderRef.current = render;

    // Create ground (shelf)
    const ground = Bodies.rectangle(400, 580, 800, 20, {
      isStatic: true,
      render: { 
        visible: false
      }
    });

    // Create books with proper dimensions and constraints
    const bookBodies = books.map(book => {
      const bookBody = Bodies.rectangle(
        book.x,
        580 - (book.height / 2),
        book.width,
        book.height,
        {
          friction: 0.8,
          restitution: 0.1,
          render: {
            fillStyle: '#ffffff',
            strokeStyle: '#2563eb',
            lineWidth: 1
          }
        }
      );

      // Add invisible constraint to keep books upright initially
      const constraint = Constraint.create({
        pointA: { x: book.x, y: 580 - (book.height / 2) },
        bodyB: bookBody,
        stiffness: 0.1,
        render: { visible: false }
      });

      booksRef.current[book.id] = { body: bookBody, constraint };
      return [bookBody, constraint];
    });

    World.add(engine.world, [ground, ...bookBodies.flat()]);

    const runner = Matter.Runner.create();
    Matter.Runner.run(runner, engine);
    Render.run(render);

    return () => {
      Render.stop(render);
      Matter.Runner.stop(runner);
      World.clear(engine.world);
      Engine.clear(engine);
      render.canvas.remove();
    };
  }, []);

  const handleBookClick = (bookId) => {
    if (!engineRef.current || selectedBook) return;

    setSelectedBook(bookId);
    const selectedBookRef = booksRef.current[bookId];
    
    if (selectedBookRef) {
      // Remove the selected book's constraint and the book itself
      World.remove(engineRef.current.world, [selectedBookRef.constraint, selectedBookRef.body]);
      delete booksRef.current[bookId];

      // Remove constraints from remaining books to let them fall naturally
      Object.values(booksRef.current).forEach(({ constraint }) => {
        World.remove(engineRef.current.world, constraint);
      });

      setTimeout(() => setShowContent(true), 500);
    }
  };

  return (
    <div className="w-full bg-blue-600 p-2 overflow-hidden">
      <div className="max-w-4xl mx-auto">
        <div className="relative">
          <div ref={sceneRef} className="w-full" />
          
          {/* Book titles overlay */}
          <div className="absolute inset-0 pointer-events-none">
            {books.map((book) => {
              const bookRef = booksRef.current[book.id];
              if (!bookRef) return null;

              const position = bookRef.body.position;
              const angle = bookRef.body.angle;

              return (
                <div
                  key={book.id}
                  onClick={() => handleBookClick(book.id)}
                  className="absolute cursor-pointer pointer-events-auto"
                  style={{
                    left: `${position.x - book.width/2}px`,
                    top: `${position.y - book.height/2}px`,
                    width: `${book.width}px`,
                    height: `${book.height}px`,
                    transform: `rotate(${angle}rad)`,
                  }}
                >
                  <span 
                    className="absolute font-mono tracking-widest text-xs text-blue-600 font-bold whitespace-nowrap"
                    style={{
                      transform: 'rotate(90deg)',
                      transformOrigin: 'left center',
                      left: '16px'
                    }}
                  >
                    {book.title}
                  </span>
                </div>
              );
            })}
          </div>
        </div>

        {/* Content panel */}
        <div 
          className={`
            fixed inset-0 bg-white
            transition-transform duration-500 ease-in-out
            ${showContent ? 'translate-y-0' : 'translate-y-full'}
          `}
        >
          {selectedBook && books.find(b => b.id === selectedBook) && (
            <div className="max-w-4xl mx-auto p-1 h-full flex flex-col">
              <div className="flex justify-between items-start mb-8">
                <h2 className="text-blue-600 text-4xl font-mono tracking-wider font-bold">
                  {books.find(b => b.id === selectedBook).title}
                </h2>
                <button 
                  onClick={() => {
                    setShowContent(false);
                    setTimeout(() => setSelectedBook(null), 500);
                  }}
                  className="text-blue-600 hover:text-blue-800 transition-colors duration-300"
                >
                  <svg 
                    className="w-8 h-8" 
                    fill="none" 
                    stroke="currentColor" 
                    viewBox="0 0 24 24"
                  >
                    <path 
                      strokeLinecap="round" 
                      strokeLinejoin="round" 
                      strokeWidth={2} 
                      d="M6 18L18 6M6 6l12 12" 
                    />
                  </svg>
                </button>
              </div>
              <div className="flex-1 overflow-auto">
                <p className="text-blue-600 font-mono text-lg leading-relaxed">
                  {books.find(b => b.id === selectedBook).content}
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default BookShelf;
