import React, { useState } from 'react';
import './index.css';


const SimplifiedBookShelf = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [firstBookRotated, setFirstBookRotated] = useState(true);

  const books = [
    { 
      id: 1, 
      title: "Projects", 
      author: "Jane Smith",
      year: 2023,
      description: "A collection of innovative design projects showcasing contemporary approaches to visual communication and digital media.",
      tags: ["Portfolio", "Design", "Digital"],
      pages: 180,
      content: "A celebration of contemporary graphic design and festival aesthetics in modern culture.",
      color: "bg-green-500",
      textColor: "text-white",
      isOblique: true
    },
    { 
      id: 2, 
      title: "Curriculum", 
      author: "John Doe",
      year: 2024,
      description: "A comprehensive exploration of design fundamentals and their practical applications in modern contexts.",
      tags: ["Design", "Education", "Theory"],
      pages: 245,
      content: "Exploring the fundamental elements of design through geometric principles and visual harmony.",
      color: "bg-yellow-500",
      textColor: "text-black",
      isOblique: false
    },
    { 
      id: 3, 
      title: "Publications", 
      author: "Robert Johnson",
      year: 2024,
      description: "An anthology of modern publishing works examining the intersection of traditional and digital mediums.",
      tags: ["Publishing", "Media", "Digital"],
      pages: 320,
      content: "The art of pencil and charcoal, examining traditional drawing techniques in the digital age.",
      color: "bg-red-500",
      textColor: "text-white",
      isOblique: false
    },
    { 
      id: 4, 
      title: "Contacts", 
      author: "Sarah Williams",
      year: 2023,
      description: "A comprehensive directory of industry professionals and creative practitioners, featuring insights and connections.",
      tags: ["Network", "Industry", "Contacts"],
      pages: 150,
      content: "Contemporary interpretations of traditional folk art patterns and methodologies.",
      color: "bg-blue-500",
      textColor: "text-white",
      isOblique: false
    }
  ];

  const handleBookClick = (bookId) => {
    if (bookId === 1) {
      if (selectedBook !== 1) {
        setFirstBookRotated(false);
        setTimeout(() => {
          setSelectedBook(1);
        }, 100);
      } else {
        setSelectedBook(null);
        setTimeout(() => {
          setFirstBookRotated(true);
        }, 300);
      }
    } else {
      setSelectedBook(selectedBook === bookId ? null : bookId);
    }
  };

  return (
    <div className="h-screen flex items-start justify-end">
      <div className="flex h-full items-stretch -translate-y-6">
        {books.map((book) => (
          <div
            key={book.id}
            className="flex h-full relative"
          >
            <div className="flex">
              <div
                onClick={() => handleBookClick(book.id)}
                className={`book cursor-pointer flex items-center justify-center transition-transform duration-300 ease-in-out
                          ${book.accentColor} 
                          ${book.id === 1 ? (book.isOblique && firstBookRotated ? '-skew-x-3' : '') : (book.isOblique ? '-skew-x-12' : '')}
                          w-12 z-10 origin-top`}
              >
                <span className={`transform whitespace-nowrap text-lg font-bold tracking-wide transition-transform duration-300 ease-in-out -rotate-90
                                ${book.id === 1 ? 
                                  (firstBookRotated && book.isOblique ? '-skew-x-12' : '') : 
                                  (book.isOblique ? '-skew-x-12' : '')}`}>
                  {book.title}
                </span>
              </div>
              
              <div 
                className={`overflow-hidden transition-all duration-300 ease-in-out
                          ${selectedBook === book.id ? 'w-96 opacity-100' : 'w-0 opacity-0'}`}
              >
                <div className={`${book.accentColor} h-full p-6 w-96`}>
                  <h2 className="text-2xl font-bold mb-2">{book.title}</h2>
                  <p className="mb-2">
                    By {book.author} ({book.year})
                  </p>
                  <p className="text-sm mb-4">{book.description}</p>
                  <div className="flex gap-2 flex-wrap mb-4">
                    {book.tags.map(tag => (
                      <span 
                        key={tag} 
                        className={`px-2 py-1 rounded-full text-xs 
                                  ${book.accentColor === 'text-black' ? 'bg-black text-white' : 'bg-white text-black'}`}
                      >
                        {tag}
                      </span>
                    ))}
                  </div>
                  <p className="text-sm">{book.content}</p>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SimplifiedBookShelf;
