import React from 'react';
import Bookshelf from './BookShelf';

const PersonalProfile = () => {
  return (
    <div className="bg-blue-600 min-h-screen flex flex-col">
      <div className="flex flex-1">
        {/* Left Column - Personal Info */}
        <div className="w-3/5 flex items-center justify-center relative">
          <div className="text-center max-w-md text-white">
            <h1 className="text-2xl mb-4">
              Hi, my name is Valentina Pasqual
            </h1>
            <p className="mb-4">
              I am a PhD student at University of Bologna. I'm mainly interested in Semantic Web technologies (LOD) applied to GLAM domain.
            </p>
            <div className="flex flex-wrap gap-2 justify-center">
              {['digital humanities', 'semantic web', 'information science', 'knowledge engineering', 'GLAM'].map((tag) => (
                <span 
                  key={tag} 
                  className="text-xs font-medium px-2.5 py-0.5 bg-white/10 rounded"
                >
                  {tag}
                </span>
              ))}
            </div>
          </div>
        </div>
        
        {/* Right Column - Personal Info */}
        <div className="w-2/5 flex items-center justify-center">
          <div className="text-white text-center">
            Additional Content
          </div>
        </div>
      </div>
      
      {/* White Line */}
      <div className="w-full h-0.5 bg-white opacity-100"></div>
      
      {/* Bookshelf at the bottom */}
      <div>
        <Bookshelf />
      </div>
    </div>
  );
};

export default PersonalProfile;
