import React, { useState, useEffect } from 'react';

const Projects = () => {
  const [projectCategories, setProjectCategories] = useState({});
  const [expandedCategory, setExpandedCategory] = useState(null);
  const [expandedProject, setExpandedProject] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('./data/projects.json')
      .then(response => response.json())
      .then(data => setProjectCategories(data))
      .catch(error => setError(error.message));
  }, []);

  if (error) return <div className="text-red-600">{error}</div>;

  const toggleCategory = (category) => {
    setExpandedCategory(expandedCategory === category ? null : category);
    setExpandedProject(null); // Close any open project when toggling category
  };

  const toggleProject = (projectName) => {
    setExpandedProject(expandedProject === projectName ? null : projectName);
  };

  return (
    <div className="space-y-8 p-4">
      {Object.entries(projectCategories).map(([category, projects]) => (
        <div key={category} className="border-l-4 border-gray-300 pl-4 mb-8">
          <h2 
            className="text-3xl book-1-accent cursor-pointer flex items-center" 
            onClick={() => toggleCategory(category)}
          >
            <span className="mr-2">{expandedCategory === category ? '▼' : '►'}</span>
            {category}
          </h2>
          
          {expandedCategory === category && (
            <div className="mt-4 space-y-6 ml-4">
              {Object.entries(projects).map(([projectName, details]) => (
                <div key={projectName} className="border-b pb-4">
                  <div 
                    className="flex justify-between items-center cursor-pointer"
                    onClick={() => toggleProject(projectName)}
                  >
                    <h3 className="text-2xl font-semibold">{projectName}</h3>
                    <span>{expandedProject === projectName ? '▼' : '►'}</span>
                  </div>
                  
                  <div className="flex flex-wrap gap-2 mt-2">
                    {details.type?.map((type, typeIndex) => (
                      <span key={typeIndex} className="px-2 py-1 bg-gray-100 text-sm rounded-md">
                        {type}
                      </span>
                    ))}
                  </div>
                  
                  {expandedProject === projectName && (
                    <div className="mt-3 space-y-3">
                      <p className="text-gray-700">{details.description || "No description available"}</p>
                      <div className="text-sm">
                        <span className="font-medium">Year:</span> {details.year} | 
                        <span className="font-medium"> Authors:</span> {details.authors}
                      </div>
                      <a 
                        href={details.link} 
                        className="inline-block mt-2 px-4 py-2 bg-gray-200 hover:bg-gray-300 transition-colors rounded"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        Learn More
                      </a>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Projects;
