import React, { useState, useEffect, useRef } from 'react';
import Matter from 'matter-js';

const BookShelf = () => {
    const [selectedBook, setSelectedBook] = useState(null);
    const [showContent, setShowContent] = useState(false);
    const sceneRef = useRef(null);
    const booksRef = useRef([]);
    
    const books = [
        { 
          id: 1, 
          title: "DOT LINE SHAPE", 
          height: "h-64", 
          width: "w-16",
          transform: "translateY(-4px) rotate(5deg)",
          marginLeft: "ml-0",
          fallRotation: 25,
          cascadeRotation: 45, // Additional rotation when hitting next book
          content: "Exploring the fundamental elements of design through geometric principles and visual harmony."
        },
        { 
          id: 2, 
          title: "GRAPHIC FEST", 
          height: "h-80", 
          width: "w-14",
          transform: "rotate(0deg)",
          marginLeft: "ml-7",
          fallRotation: 20,
          cascadeRotation: 50,
          content: "A celebration of contemporary graphic design and festival aesthetics in modern culture."
        },
        { 
          id: 3, 
          title: "GRAPHITE", 
          height: "h-48", 
          width: "w-24",
          transform: "rotate(0deg)",
          marginLeft: "ml-1",
          fallRotation: 30,
          cascadeRotation: 55,
          content: "The art of pencil and charcoal, examining traditional drawing techniques in the digital age."
        },
        { 
          id: 4, 
          title: "NEW FOLK ART", 
          height: "h-72", 
          width: "w-20",
          transform: "translateY(-9px) rotate(-8deg)",
          marginLeft: "ml-5",
          fallRotation: 22,
          cascadeRotation: 60,
          content: "Contemporary interpretations of traditional folk art patterns and methodologies."
        }
      ];
  
    useEffect(() => {
      const Engine = Matter.Engine;
      const Render = Matter.Render;
      const World = Matter.World;
      const Bodies = Matter.Bodies;
      const Body = Matter.Body;
  
      const engine = Engine.create();
      const render = Render.create({
        element: sceneRef.current,
        engine: engine,
        options: {
          width: 800,
          height: 400,
          wireframes: false,
          background: 'transparent',
        },
      });
  
      const shelfBody = Bodies.rectangle(400, 380, 560, 20, {
        isStatic: true,
        render: { fillStyle: '#fff' },
      });
      World.add(engine.world, shelfBody);
  
      booksRef.current = books.map((book) => {
        const bookBody = Bodies.rectangle(
          400 + book.marginLeft,
          300,
          book.width,
          book.height,
          {
            frictionAir: 0.05,
            render: { fillStyle: '#fff' },
          }
        );
        World.add(engine.world, bookBody);
        return bookBody;
      });
  
      const resetBooks = () => {
        booksRef.current.forEach((bookBody, index) => {
          Body.setPosition(bookBody, { x: 400 + books[index].marginLeft, y: 300 });
          Body.setVelocity(bookBody, { x: 0, y: 0 });
          Body.setAngle(bookBody, 0);
        });
      };
  
      const handleBookClick = (bookId) => {
        if (selectedBook === bookId) {
          setShowContent(false);
          setTimeout(() => {
            setSelectedBook(null);
            resetBooks();
          }, 500);
        } else {
          setSelectedBook(bookId);
          const bookIndex = books.findIndex((b) => b.id === bookId);
          const selectedBody = booksRef.current[bookIndex];
          Body.applyForce(selectedBody, selectedBody.position, { x: 0, y: -0.15 });
  
          booksRef.current.forEach((body, index) => {
            if (index !== bookIndex) {
              const fallDirection = index < bookIndex ? -1 : 1;
              Body.applyForce(body, body.position, {
                x: fallDirection * 0.01,
                y: 0.02,
              });
            }
          });
  
          setTimeout(() => {
            setShowContent(true);
          }, 1000);
        }
      };
  
      Matter.Runner.run(engine);
      Render.run(render);
  
      return () => {
        Render.stop(render);
        World.clear(engine.world);
        Engine.clear(engine);
      };
    }, []);
  
    const selectedBookData = books.find((b) => b.id === selectedBook);
  
    return (
      <div className="w-full min-h-screen bg-blue-600 p-8 overflow-hidden">
        <div className="max-w-4xl mx-auto">
          <div ref={sceneRef} className="relative h-96 mb-8" />
  
          {books.map((book) => (
            <div
              key={book.id}
              onClick={() => handleBookClick(book.id)}
              className={`
                ${book.width} ${book.height} 
                bg-white text-blue-600 px-3 cursor-pointer
                absolute top-0 left-0 
              `}
              style={{ 
                transformOrigin: 'center',
                marginLeft: book.marginLeft,
              }}
            >
              <span className="absolute rotate-90 origin-left whitespace-nowrap font-mono tracking-widest text-xs left-4 text-blue-600 font-bold">
                {book.title}
              </span>
            </div>
          ))}
  
          <div
            className={`
              fixed inset-0 bg-white 
              transition-transform duration-500 ease-in-out
              ${showContent ? 'translate-y-0' : 'translate-y-full'}
            `}
          >
            {selectedBookData && (
              <div className="max-w-4xl mx-auto p-8 h-full flex flex-col">
                <div className="flex justify-between items-start mb-8">
                  <h2 className="text-blue-600 text-4xl font-mono tracking-wider font-bold">
                    {selectedBookData.title}
                  </h2>
                  <button
                    onClick={() => handleBookClick(selectedBook)}
                    className="text-blue-600 hover:text-blue-800 transition-colors duration-300"
                  >
                    <svg
                      className="w-8 h-8"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M6 18L18 6M6 6l12 12"
                      />
                    </svg>
                  </button>
                </div>
                <div className="flex-1 overflow-auto">
                  <p className="text-blue-600 font-mono text-lg leading-relaxed">
                    {selectedBookData.content}
                  </p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  };
  
  export default BookShelf;
