import React from 'react';
import Bookshelf from './Bookshelf'; // Assuming the Bookshelf component exists

const PersonalProfile = () => {
  return (
    <div className="flex min-h-screen">
      {/* Left Column - Personal Info */}
      <div className="w-1/3 bg-gray-100 p-6">
        <div className="text-left">
          <h1 className="text-2xl font-bold mb-4">
            Hi, my name is <span className="text-blue-600">Valentina Pasqual</span>
          </h1>
          <p className="mb-4">
            I am a PhD student at University of Bologna. I'm mainly interested in Semantic Web technologies (LOD) applied to GLAM domain.
          </p>
          <div className="flex flex-wrap gap-2">
            {['digital humanities', 'semantic web', 'information science', 'knowledge engineering', 'GLAM'].map((tag) => (
              <span 
                key={tag} 
                className="bg-blue-100 text-blue-800 text-xs font-medium px-2.5 py-0.5 rounded"
              >
                {tag}
              </span>
            ))}
          </div>
        </div>
      </div>
      
      {/* Right Column - Bookshelf */}
      <div className="w-2/3 p-6">
        <Bookshelf />
      </div>
    </div>
  );
};

export default PersonalProfile;
