import React, { useState, useEffect } from 'react';

const Projects = () => {
  const [projects, setProjects] = useState({});
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('./data/projects.json')
      .then(response => response.json())
      .then(data => setProjects(data))
      .catch(error => setError(error.message));
  }, []);

  if (error) {
    return <div className="text-red-600">Error loading projects: {error}</div>;
  }

  const projectArray = Object.entries(projects).map(([name, data]) => ({
    name,
    ...data
  }));

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 p-4">
      {projectArray.map((project, index) => (
        <div key={index} className="bg-white rounded-lg shadow-md">
          <div className="p-6">
            <h5 className="text-xl font-bold mb-2">{project.name}</h5>
            <div className="space-x-2 mb-4">
              {project.type?.map((type, typeIndex) => (
                <span 
                  key={typeIndex}
                  className="inline-block bg-blue-500 text-white px-3 py-1 rounded-full text-sm"
                >
                  {type}
                </span>
              ))}
            </div>
            <p className="text-gray-700 mb-4">{project.description}</p>
            <p className="text-gray-600 mb-2">Year: {project.year}</p>
            <p className="text-gray-600 mb-4">Authors: {project.authors}</p>
            <a 
              href={project.link} 
              className="inline-block bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded transition-colors"
            >
              Learn More
            </a>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Projects;
