import React, { useEffect, useRef, useState } from 'react';
import Matter from 'matter-js';

const PhysicsBookshelf = () => {
  const sceneRef = useRef(null);
  const engineRef = useRef(null);
  const renderRef = useRef(null);
  const runnerRef = useRef(null);
  const worldRef = useRef(null);
  const [selectedBook, setSelectedBook] = useState(null);
  const [showContent, setShowContent] = useState(false);
  const [isInitialized, setIsInitialized] = useState(false);
  const booksRef = useRef({});
  
  const books = [
    { 
      id: 1, 
      title: "Curriculum", 
      width: 40,
      height: 160,
      author: "John Doe",
      year: 2024,
      description: "A comprehensive exploration of design fundamentals and their practical applications in modern contexts. This curriculum covers everything from basic principles to advanced techniques.",
      tags: ["Design", "Education", "Theory"],
      pages: 245,
      content: "Exploring the fundamental elements of design through geometric principles and visual harmony."
    },
    { 
      id: 2, 
      title: "Projects", 
      width: 35,
      height: 200,
      author: "Jane Smith",
      year: 2023,
      description: "A collection of innovative design projects showcasing contemporary approaches to visual communication and digital media.",
      tags: ["Portfolio", "Design", "Digital"],
      pages: 180,
      content: "A celebration of contemporary graphic design and festival aesthetics in modern culture."
    },
    { 
      id: 3, 
      title: "Publications", 
      width: 60,
      height: 120,
      author: "Robert Johnson",
      year: 2024,
      description: "An anthology of modern publishing works examining the intersection of traditional and digital mediums.",
      tags: ["Publishing", "Media", "Digital"],
      pages: 320,
      content: "The art of pencil and charcoal, examining traditional drawing techniques in the digital age."
    },
    { 
      id: 4, 
      title: "Contacts", 
      width: 50,
      height: 180,
      author: "Sarah Williams",
      year: 2023,
      description: "A comprehensive directory of industry professionals and creative practitioners, featuring insights and connections.",
      tags: ["Network", "Industry", "Contacts"],
      pages: 150,
      content: "Contemporary interpretations of traditional folk art patterns and methodologies."
    }
  ];

  // Initialize physics engine and scene only once
  useEffect(() => {
    if (engineRef.current) return; // Prevent re-initialization
    
    const Engine = Matter.Engine;
    const Render = Matter.Render;
    const Runner = Matter.Runner;
    const World = Matter.World;
    const Bodies = Matter.Bodies;
    
    // Create engine
    engineRef.current = Engine.create({
      gravity: { x: 0, y: 2 }
    });
    
    // Create renderer
    renderRef.current = Render.create({
      element: sceneRef.current,
      engine: engineRef.current,
      options: {
        width: 800,
        height: 400,
        wireframes: false,
        background: '#2563eb',
        pixelRatio: window.devicePixelRatio // Add proper pixel ratio support
      }
    });

    // Add mouse control
    const mouse = Matter.Mouse.create(renderRef.current.canvas);
    const mouseConstraint = Matter.MouseConstraint.create(engineRef.current, {
      mouse: mouse,
      constraint: {
        stiffness: 0.2,
        render: {
          visible: false
        }
      }
    });

    World.add(engineRef.current.world, mouseConstraint);
    renderRef.current.mouse = mouse;

    // Handle clicks on books
    Matter.Events.on(mouseConstraint, 'mousedown', (event) => {
      const clickedBody = mouseConstraint.body;
      if (clickedBody) {
        const bookId = parseInt(clickedBody.label.split('-')[1]);
        handleBookClick(bookId);
      }
    });

    // Create static bodies
    const shelf = Bodies.rectangle(300, 350, 300, 20, { 
      isStatic: true,
      angle: Math.PI * 0.02,
      render: { fillStyle: '#2563eb' }
    });
    
    const ground = Bodies.rectangle(400, 390, 810, 20, { 
      isStatic: true,
      render: { fillStyle: '#2563eb' }
    });

    const leftWall = Bodies.rectangle(0, 200, 20, 400, { 
      isStatic: true,
      render: { fillStyle: '#2563eb' }
    });
    
    const rightBoundary = Bodies.rectangle(450, 200, 4, 300, { 
      isStatic: true,
      render: { fillStyle: '#ffffff' }
    });
    
    const rightWall = Bodies.rectangle(800, 200, 20, 400, { 
      isStatic: true,
      render: { fillStyle: '#2563eb' }
    });
    
    World.add(engineRef.current.world, [ground, shelf, leftWall, rightWall, rightBoundary]);
    
    // Create books
    books.forEach((book, index) => {
      const xPos = 250 + (index * 40);
      const xOffset = Math.random() * 30 - 15;
      const bookBody = Bodies.rectangle(xPos + xOffset, -100 - (index * 50), book.width, book.height, {
        render: {
          fillStyle: '#ffffff',
          strokeStyle: '#ffffff',
          lineWidth: 1
        },
        chamfer: { radius: 2 },
        friction: 0.9,
        restitution: 0.1,
        density: 0.002,
        frictionAir: 0.001,
        slop: 0.05,
        label: `book-${book.id}`
      });
      
      booksRef.current[book.id] = bookBody;
      World.add(engineRef.current.world, bookBody);
    });
    
    // Create and store runner
    runnerRef.current = Runner.create();
    Runner.run(runnerRef.current, engineRef.current);
    Render.run(renderRef.current);
    
    setIsInitialized(true);
    
    return () => {
      if (engineRef.current) {
        World.clear(engineRef.current.world);
        Engine.clear(engineRef.current);
      }
      if (renderRef.current) {
        Render.stop(renderRef.current);
        renderRef.current.canvas.remove();
      }
      if (runnerRef.current) {
        Runner.stop(runnerRef.current);
      }
    };
  }, []); // Empty dependency array to run only once

  const handleBookClick = (bookId) => {
    if (selectedBook === bookId) {
      setShowContent(false);
      setTimeout(() => {
        setSelectedBook(null);
      }, 500);
      return;
    }

    const selectedBody = booksRef.current[bookId];
    if (!selectedBody) return;

    Matter.Body.setVelocity(selectedBody, { x: 0, y: -15 });
    
    setTimeout(() => {
      Matter.World.remove(engineRef.current.world, selectedBody);
      delete booksRef.current[bookId];
      setSelectedBook(bookId);
      setShowContent(true);
    }, 300);
  };

  return (
    <div className="w-full bg-blue-600 p-2 overflow-hidden">
      <div className="max-w-4xl mx-auto">
        <div ref={sceneRef} className="relative h-96" style={{ touchAction: 'none' }} />
        
        {isInitialized && (
          <div className="absolute inset-0 pointer-events-none" style={{ touchAction: 'none' }}>
            {books.map((book) => {
              const body = booksRef.current[book.id];
              if (!body) return null;
              
              const { position, angle } = body;
              return (
                <div
                  key={book.id}
                  className="absolute pointer-events-auto cursor-pointer z-10 bg-transparent"
                  style={{
                    left: `${position.x}px`,
                    top: `${position.y}px`,
                    transform: `translate(-50%, -50%) rotate(${angle}rad)`,
                    width: `${book.width}px`,
                    height: `${book.height}px`,
                  }}
                  onClick={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                    handleBookClick(book.id);
                  }}
                >
                  <span className="absolute rotate-90 origin-left whitespace-nowrap font-mono tracking-widest text-xs left-4 text-blue-600 font-bold select-none pointer-events-none">
                    {book.title}
                  </span>
                  <div className="absolute inset-0 hover:bg-white hover:bg-opacity-20" />
                </div>
              );
            })}
          </div>
        )}

        {/* Content overlay */}
        <div className={`fixed inset-0 bg-white transition-all duration-500 ease-in-out ${showContent ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
          {selectedBook && (
            <div className="max-w-4xl mx-auto p-8 h-full flex flex-col">
              <div className="flex justify-between items-start mb-8">
                <div>
                  <h2 className="text-blue-600 text-4xl font-mono tracking-wider font-bold mb-2">
                    {books.find(b => b.id === selectedBook)?.title}
                  </h2>
                  <div className="text-blue-500 font-mono">
                    <p>By {books.find(b => b.id === selectedBook)?.author} ({books.find(b => b.id === selectedBook)?.year})</p>
                    <p>{books.find(b => b.id === selectedBook)?.pages} pages</p>
                  </div>
                </div>
                <button 
                  onClick={() => handleBookClick(selectedBook)}
                  className="text-blue-600 hover:text-blue-800 transition-colors duration-300"
                >
                  <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              <div className="flex-1 overflow-auto">
                <div className="prose prose-blue max-w-none">
                  <p className="text-blue-600 text-lg leading-relaxed mb-6">
                    {books.find(b => b.id === selectedBook)?.description}
                  </p>
                  <div className="mb-6">
                    <h3 className="text-blue-600 font-mono text-xl mb-2">Tags</h3>
                    <div className="flex gap-2 flex-wrap">
                      {books.find(b => b.id === selectedBook)?.tags.map(tag => (
                        <span key={tag} className="bg-blue-100 text-blue-600 px-3 py-1 rounded-full text-sm">
                          {tag}
                        </span>
                      ))}
                    </div>
                  </div>
                  <div>
                    <h3 className="text-blue-600 font-mono text-xl mb-2">Preview</h3>
                    <p className="text-blue-600 font-mono">
                      {books.find(b => b.id === selectedBook)?.content}
                    </p>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PhysicsBookshelf;
