import React, { useEffect, useRef, useState } from 'react';
import Matter from 'matter-js';

const BookShelf = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [showContent, setShowContent] = useState(false);
  const sceneRef = useRef(null);
  const engineRef = useRef(null);
  const booksRef = useRef({});
  
  const books = [
    { 
      id: 1, 
      title: "Curriculum", 
      width: 64, // w-16 = 4rem = 64px
      height: 256, // h-64 = 16rem = 256px
      x: 100,
      content: "Exploring the fundamental elements of design through geometric principles and visual harmony."
    },
    { 
      id: 2, 
      title: "Projects", 
      width: 56, // w-14 = 3.5rem = 56px
      height: 320, // h-80 = 20rem = 320px
      x: 180,
      content: "A celebration of contemporary graphic design and festival aesthetics in modern culture."
    },
    { 
      id: 3, 
      title: "Publications", 
      width: 96, // w-24 = 6rem = 96px
      height: 192, // h-48 = 12rem = 192px
      x: 280,
      content: "The art of pencil and charcoal, examining traditional drawing techniques in the digital age."
    },
    { 
      id: 4, 
      title: "Contacts", 
      width: 80, // w-20 = 5rem = 80px
      height: 288, // h-72 = 18rem = 288px
      x: 380,
      content: "Contemporary interpretations of traditional folk art patterns and methodologies."
    }
  ];

  useEffect(() => {
    // Matter.js setup
    const Engine = Matter.Engine;
    const Render = Matter.Render;
    const World = Matter.World;
    const Bodies = Matter.Bodies;

    // Create engine
    const engine = Engine.create({
      gravity: { x: 0, y: 1, scale: 0.001 }
    });
    engineRef.current = engine;

    // Create renderer
    const render = Render.create({
      element: sceneRef.current,
      engine: engine,
      options: {
        width: 800,
        height: 600,
        wireframes: false,
        background: 'transparent'
      }
    });

    // Create ground (shelf)
    const ground = Bodies.rectangle(400, 580, 800, 20, {
      isStatic: true,
      render: { fillStyle: '#2563eb' }
    });

    // Create books
    const bookBodies = books.map(book => {
      const bookBody = Bodies.rectangle(
        book.x,
        580 - (book.height / 2),
        book.width,
        book.height,
        {
          friction: 0.5,
          restitution: 0.1,
          render: {
            fillStyle: '#ffffff',
            strokeStyle: '#2563eb',
            lineWidth: 1
          }
        }
      );
      booksRef.current[book.id] = bookBody;
      return bookBody;
    });

    // Add all bodies to world
    World.add(engine.world, [ground, ...bookBodies]);

    // Create and run the runner
    const runner = Matter.Runner.create();
    Matter.Runner.run(runner, engine);
    Render.run(render);

    return () => {
      // Cleanup
      Render.stop(render);
      Matter.Runner.stop(runner);
      World.clear(engine.world);
      Engine.clear(engine);
      render.canvas.remove();
    };
  }, []);

  const handleBookClick = (bookId) => {
    if (selectedBook === bookId) {
      setShowContent(false);
      setTimeout(() => setSelectedBook(null), 500);
      return;
    }

    setSelectedBook(bookId);
    const selectedBookBody = booksRef.current[bookId];
    
    // Apply impulse to selected book upward
    Matter.Body.setVelocity(selectedBookBody, { x: 0, y: -10 });
    
    // Make other books fall with different impulses
    Object.entries(booksRef.current).forEach(([id, body]) => {
      if (parseInt(id) !== bookId) {
        const direction = parseInt(id) < bookId ? -1 : 1;
        Matter.Body.setVelocity(body, {
          x: direction * (Math.random() * 5 + 2),
          y: -2
        });
      }
    });

    setTimeout(() => setShowContent(true), 500);
  };

  const selectedBookData = books.find(b => b.id === selectedBook);

  return (
    <div className="w-full bg-blue-600 p-2 overflow-hidden">
      <div className="max-w-4xl mx-auto">
        <div className="relative">
          <div ref={sceneRef} className="w-full" />
          
          {/* Book titles overlay */}
          <div className="absolute inset-0 pointer-events-none">
            {books.map((book) => (
              <div
                key={book.id}
                onClick={() => handleBookClick(book.id)}
                className="absolute cursor-pointer pointer-events-auto"
                style={{
                  left: `${book.x - book.width/2}px`,
                  top: `${280 - book.height}px`,
                  width: `${book.width}px`,
                  height: `${book.height}px`
                }}
              >
                <span className="absolute rotate-90 origin-left whitespace-nowrap font-mono tracking-widest text-xs left-4 text-blue-600 font-bold">
                  {book.title}
                </span>
              </div>
            ))}
          </div>
        </div>

        {/* Content panel */}
        <div 
          className={`
            fixed inset-0 bg-white
            transition-transform duration-500 ease-in-out
            ${showContent ? 'translate-y-0' : 'translate-y-full'}
          `}
        >
          {selectedBookData && (
            <div className="max-w-4xl mx-auto p-1 h-full flex flex-col">
              <div className="flex justify-between items-start mb-8">
                <h2 className="text-blue-600 text-4xl font-mono tracking-wider font-bold">
                  {selectedBookData.title}
                </h2>
                <button 
                  onClick={() => handleBookClick(selectedBook)}
                  className="text-blue-600 hover:text-blue-800 transition-colors duration-300"
                >
                  <svg 
                    className="w-8 h-8" 
                    fill="none" 
                    stroke="currentColor" 
                    viewBox="0 0 24 24"
                  >
                    <path 
                      strokeLinecap="round" 
                      strokeLinejoin="round" 
                      strokeWidth={2} 
                      d="M6 18L18 6M6 6l12 12" 
                    />
                  </svg>
                </button>
              </div>
              <div className="flex-1 overflow-auto">
                <p className="text-blue-600 font-mono text-lg leading-relaxed">
                  {selectedBookData.content}
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default BookShelf;
