import React, { useState, useEffect } from 'react';
import Matter from 'matter-js';


const BookShelf = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [fallingBooks, setFallingBooks] = useState([]);
  const [showContent, setShowContent] = useState(false);
  
  const books = [
    { 
      id: 1, 
      title: "Curriculum", 
      height: "h-64", 
      width: "w-16",
      transform: "translateY(-4px) rotate(5deg)",
      marginLeft: "ml-0",
      fallRotation: 25,
      cascadeRotation: 45, // Additional rotation when hitting next book
      content: "Exploring the fundamental elements of design through geometric principles and visual harmony."
    },
    { 
      id: 2, 
      title: "GRAPHIC FEST", 
      height: "h-80", 
      width: "w-14",
      transform: "rotate(0deg)",
      marginLeft: "ml-7",
      fallRotation: 20,
      cascadeRotation: 50,
      content: "A celebration of contemporary graphic design and festival aesthetics in modern culture."
    },
    { 
      id: 3, 
      title: "GRAPHITE", 
      height: "h-48", 
      width: "w-24",
      transform: "rotate(0deg)",
      marginLeft: "ml-1",
      fallRotation: 30,
      cascadeRotation: 55,
      content: "The art of pencil and charcoal, examining traditional drawing techniques in the digital age."
    },
    { 
      id: 4, 
      title: "NEW FOLK ART", 
      height: "h-72", 
      width: "w-20",
      transform: "translateY(-9px) rotate(-8deg)",
      marginLeft: "ml-5",
      fallRotation: 22,
      cascadeRotation: 60,
      content: "Contemporary interpretations of traditional folk art patterns and methodologies."
    }
  ];

  const handleBookClick = (bookId) => {
    if (selectedBook === bookId) {
      setShowContent(false);
      setTimeout(() => {
        setSelectedBook(null);
        setFallingBooks([]);
      }, 500);
    } else {
      setSelectedBook(bookId);
      const bookIndex = books.findIndex(b => b.id === bookId);
      let fallingSequence = [];
      
      if (bookIndex === 0) {
        fallingSequence = books.slice(1).map(b => b.id);
      } else {
        const leftBooks = books.slice(0, bookIndex).map(b => b.id).reverse();
        const rightBooks = books.slice(bookIndex + 1).map(b => b.id);
        fallingSequence = [...leftBooks, ...rightBooks];
      }
      
      // Trigger falling animations with progressive delays
      fallingSequence.forEach((id, index) => {
        setTimeout(() => {
          setFallingBooks(prev => [...prev, id]);
        }, index * 150); // Increased delay for more natural cascade
      });

      setTimeout(() => {
        setShowContent(true);
      }, 500);
    }
  };

  useEffect(() => {
    if (!selectedBook) {
      setTimeout(() => {
        setFallingBooks([]);
      }, 300);
    }
  }, [selectedBook]);

  const selectedBookData = books.find(b => b.id === selectedBook);

  return (
    <div className="w-full bg-blue-600 p-2 overflow-hidden">
      <div className="max-w-4xl mx-auto">
        <div className="relative px-12">
          <div className="flex items-end justify-center">
            {books.map((book) => {
              const isSelected = book.id === selectedBook;
              let transformStyle = book.transform;
              let originStyle = "bottom";
              
              if (isSelected) {
                transformStyle = `${book.transform} translateY(-16rem)`;
              } else if (fallingBooks.includes(book.id)) {
                const fallDirection = book.id < selectedBook ? 1 : -1;
                const bookIndex = fallingBooks.indexOf(book.id);
                const rotationAngle = bookIndex === 0 
                  ? book.fallRotation 
                  : book.cascadeRotation; // Use increased rotation for cascade effect
                
                transformStyle = `rotate(${fallDirection * rotationAngle}deg)`;
                originStyle = book.id < selectedBook ? "bottom right" : "bottom left";
              }

              return (
                <div
                  key={book.id}
                  onClick={() => handleBookClick(book.id)}
                  style={{
                    transform: transformStyle,
                    transformOrigin: originStyle
                  }}
                  className={`
                    bg-white text-blue-600 px-3 cursor-pointer 
                    transition-all duration-500 ease-out
                    hover:-translate-y-4
                    ${book.height} ${book.width} ${book.marginLeft}
                    relative flex items-center
                    ${isSelected ? 'opacity-0' : 'opacity-100'}
                  `}
                >
                  <span className="absolute rotate-90 origin-left whitespace-nowrap font-mono tracking-widest text-xs left-4 text-blue-600 font-bold">
                    {book.title}
                  </span>
                </div>
              );
            })}
          </div>
          {/* <div className="absolute bottom-0 left-8 right-8 h-0.5 bg-white"></div> */}
        </div>

        <div 
          className={`
            fixed inset-0 bg-white
            transition-transform duration-500 ease-in-out
            ${showContent ? 'translate-y-0' : 'translate-y-full'}
          `}
        >
          {selectedBookData && (
            <div className="max-w-4xl mx-auto p-1 h-full flex flex-col">
              <div className="flex justify-between items-start mb-8">
                <h2 className="text-blue-600 text-4xl font-mono tracking-wider font-bold">
                  {selectedBookData.title}
                </h2>
                <button 
                  onClick={() => handleBookClick(selectedBook)}
                  className="text-blue-600 hover:text-blue-800 transition-colors duration-300"
                >
                  <svg 
                    className="w-8 h-8" 
                    fill="none" 
                    stroke="currentColor" 
                    viewBox="0 0 24 24"
                  >
                    <path 
                      strokeLinecap="round" 
                      strokeLinejoin="round" 
                      strokeWidth={2} 
                      d="M6 18L18 6M6 6l12 12" 
                    />
                  </svg>
                </button>
              </div>
              <div className="flex-1 overflow-auto">
                <p className="text-blue-600 font-mono text-lg leading-relaxed">
                  {selectedBookData.content}
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default BookShelf;
