import React from 'react';
import Bookshelf from './BookShelf';

const PersonalProfile = () => {
  return (
    <div className="h-screen bg-blue-600 flex items-center justify-center">
      <div className="w-full h-[90vh] bg-blue-600 flex flex-col">
        {/* Main Content Wrapper */}
        <div className="flex flex-1">
          {/* Left Column - Personal Info */}
          <div className="w-3/5 flex items-center justify-center relative">
            <div className="text-left max-w-md text-white">
              <h1 className="text-2xl mb-4">
                Hello, I'm Adam
              </h1>
              <p className="mb-4">
                - an Art Director & Designer with a Focus on UX Design & Branding.
              </p>
              <div className="flex flex-wrap gap-2 justify-center">
                {['Digital Humanities', 'Semantic Web', 'Information Science', 'Knowledge Engineering', 'GLAM'].map((tag) => (
                  <span 
                    key={tag} 
                    className="text-xs font-medium px-2.5 py-0.5 bg-white/10 rounded"
                  >
                    {tag}
                  </span>
                ))}
              </div>
              <div className="social d-flex align-items-center justify-content-center">
                <a href="https://github.com/ValentinaPasqual" target="_blank"><i className="bi bi-github"></i></a>
                <a href="https://twitter.com/valepasqual" target="_blank"><i className="bi bi-twitter"></i></a>
                <a href="https://www.linkedin.com/in/valentina-pasqual-96791017b/" target="_blank"><i className="bi bi-linkedin"></i></a>
                <a href="https://scholar.google.com/citations?user=EVUFJkAAAAAJ&amp;hl" target="_blank"><i className="ai ai-google-scholar"></i></a>
                <a href="https://figshare.com/authors/Valentina_Pasqual/10212998" target="_blank"><i className="ai ai-figshare"></i></a>
                <a href="https://orcid.org/0000-0001-5931-5187" target="_blank"><i className="ai ai-orcid"></i></a>
              </div>
            </div>
          </div>

          <div className="w-2/5 flex flex-col">
            <div className="flex-1"></div>
            <Bookshelf />
          </div>
        </div>

        {/* Decorative Lines Container */}
        <div className="w-full h-1 bg-white"></div>
      </div>
    </div>
  );
};

export default PersonalProfile;
