import React, { useState, useEffect } from 'react';

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('.data/projects.json')
      .then(response => {
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        return response.json();
      })
      .then(data => {
        setProjects(Object.entries(data).map(([name, data]) => ({ name, ...data })));
        setLoading(false);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="p-4">Loading projects...</div>;
  if (error) return <div className="p-4 border border-red-300 bg-red-50 text-red-700 rounded">{error}</div>;

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 p-6">
      {projects.map((project) => (
        <div key={project.name} className="bg-white rounded-lg shadow-lg hover:shadow-xl transition-shadow">
          <div className="p-6">
            <h5 className="text-xl font-bold mb-3 text-gray-900">{project.name}</h5>
            <div className="flex flex-wrap gap-2 mb-4">
              {project.type?.map((type) => (
                <span 
                  key={type}
                  className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm font-medium"
                >
                  {type}
                </span>
              ))}
            </div>
            <p className="text-gray-700 mb-4">
              {project.description || "Description coming soon..."}
            </p>
            <p className="text-gray-600 mb-2">
              <span className="font-medium">Year:</span> {project.year}
            </p>
            <p className="text-gray-600 mb-4">
              <span className="font-medium">Authors:</span> {project.authors}
            </p>
            {project.link && (
              <a 
                href={project.link} 
                target="_blank"
                rel="noopener noreferrer"
                className="inline-flex items-center bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded transition-colors"
              >
                View Project
              </a>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Projects;
