import React, { useEffect, useState } from 'react';

const Curriculum = () => {
  const [employments, setEmployments] = useState([]);
  const [educations, setEducations] = useState([]);
  const [qualifications, setQualifications] = useState([]);

  useEffect(() => {
    const fetchOrcidData = async () => {
      const orcidId = '0000-0001-5931-5187';
      const apiUrl = `https://pub.orcid.org/v3.0/${orcidId}`;
      const response = await fetch(apiUrl, {
        headers: {
          Accept: 'application/json',
        },
      });
      const data = await response.json();

      setEmployments(data['activities-summary']?.employments?.['affiliation-group'] || []);
      setEducations(data['activities-summary']?.educations?.['affiliation-group'] || []);
      setQualifications(data['activities-summary']?.qualifications?.['affiliation-group'] || []);
    };

    fetchOrcidData();
  }, []);

  const formatDate = (date) => {
    if (!date) return '';
    const { year, month, day } = date;
    return toString(year,month,dat)
  };

  return (
    <div className="space-y-8">
      <div>
        <h2 className="text-2xl font-bold mb-4">Employment</h2>
        {employments.map((employment, index) => (
          <div key={index} className="mb-4">
            <p className="font-medium">{employment['summaries'][0]['employment-summary']['organization']['name']}</p>
            <p>{employment['summaries'][0]['employment-summary']['department-name']}</p>
            <p>{employment['summaries'][0]['employment-summary']['role-title']}</p>
            <p>
              {formatDate(employment['summaries'][0]['employment-summary']['start-date'])} -{' '}
              {formatDate(employment['summaries'][0]['employment-summary']['end-date']) || 'Present'}
            </p>
          </div>
        ))}
      </div>
      <div>
        <h2 className="text-2xl font-bold mb-4">Education</h2>
        {educations.map((education, index) => (
          <div key={index} className="mb-4">
            <p className="font-medium">{education['summaries'][0]['education-summary']['organization']['name']}</p>
            <p>{education['summaries'][0]['education-summary']['department-name']}</p>
            <p>{education['summaries'][0]['education-summary']['role-title']}</p>
            <p>
              {formatDate(education['summaries'][0]['education-summary']['start-date'])} -{' '}
              {formatDate(education['summaries'][0]['education-summary']['end-date']) || 'Present'}  
            </p>
          </div>
        ))}
      </div>
      <div>
      </div>
    </div>
  );
};

export default Curriculum;
