import React, { useEffect, useRef, useState, useLayoutEffect } from 'react';
import Matter from 'matter-js';

const PhysicsBookshelf = () => {
  const physicsRef = useRef({
    engine: null,
    render: null,
    runner: null,
    books: {},
    initialized: false
  });
  
  const sceneRef = useRef(null);
  const [selectedBook, setSelectedBook] = useState(null);
  const [showContent, setShowContent] = useState(false);
  const [removedBooks, setRemovedBooks] = useState(new Set()); // Track removed books
  
  const books = [
    // ... your existing books array stays the same
  ];

  useLayoutEffect(() => {
    if (physicsRef.current.initialized) return;
    
    const Engine = Matter.Engine;
    const Render = Matter.Render;
    const Runner = Matter.Runner;
    const World = Matter.World;
    const Bodies = Matter.Bodies;

    const engine = Engine.create({
      gravity: { x: 0, y: 2 }
    });
    
    const render = Render.create({
      element: sceneRef.current,
      engine: engine,
      options: {
        width: 800,
        height: 400,
        wireframes: false,
        background: '#2563eb',
        pixelRatio: window.devicePixelRatio
      }
    });

    // Updated mouse control with proper offset calculation
    const mouse = Matter.Mouse.create(render.canvas);
    const mouseConstraint = Matter.MouseConstraint.create(engine, {
      mouse: mouse,
      constraint: {
        stiffness: 0.2,
        render: {
          visible: false
        }
      }
    });

    // Update mouse position relative to canvas
    Matter.Events.on(render.canvas, 'mousedown', (event) => {
      const canvasBounds = render.canvas.getBoundingClientRect();
      const scaledX = (event.clientX - canvasBounds.left) * (render.options.width / canvasBounds.width);
      const scaledY = (event.clientY - canvasBounds.top) * (render.options.height / canvasBounds.height);
      
      // Find clicked body using scaled coordinates
      const bodies = Matter.Composite.allBodies(engine.world);
      const clickedBody = bodies.find(body => {
        return Matter.Bounds.contains(body.bounds, { x: scaledX, y: scaledY }) &&
               Matter.Vertices.contains(body.vertices, { x: scaledX, y: scaledY });
      });

      if (clickedBody && clickedBody.label.startsWith('book-')) {
        const bookId = parseInt(clickedBody.label.split('-')[1]);
        handleBookClick(bookId);
      }
    });

    World.add(engine.world, mouseConstraint);
    render.mouse = mouse;

    // Create static bodies (same as before)
    const shelf = Bodies.rectangle(300, 350, 300, 20, { 
      isStatic: true,
      angle: Math.PI * 0.02,
      render: { fillStyle: '#2563eb' }
    });
    
    const ground = Bodies.rectangle(400, 390, 810, 20, { 
      isStatic: true,
      render: { fillStyle: '#2563eb' }
    });

    const leftWall = Bodies.rectangle(0, 200, 20, 400, { 
      isStatic: true,
      render: { fillStyle: '#2563eb' }
    });
    
    const rightBoundary = Bodies.rectangle(450, 200, 4, 300, { 
      isStatic: true,
      render: { fillStyle: '#ffffff' }
    });
    
    const rightWall = Bodies.rectangle(800, 200, 20, 400, { 
      isStatic: true,
      render: { fillStyle: '#2563eb' }
    });

    World.add(engine.world, [ground, shelf, leftWall, rightWall, rightBoundary]);

    // Create only non-removed books
    books.forEach((book, index) => {
      if (!removedBooks.has(book.id)) {
        const xPos = 250 + (index * 40);
        const xOffset = Math.random() * 30 - 15;
        const bookBody = Bodies.rectangle(xPos + xOffset, -100 - (index * 50), book.width, book.height, {
          render: {
            fillStyle: '#ffffff',
            strokeStyle: '#ffffff',
            lineWidth: 1
          },
          chamfer: { radius: 2 },
          friction: 0.9,
          restitution: 0.1,
          density: 0.002,
          frictionAir: 0.001,
          slop: 0.05,
          label: `book-${book.id}`
        });
        
        physicsRef.current.books[book.id] = bookBody;
        World.add(engine.world, bookBody);
      }
    });

    physicsRef.current.engine = engine;
    physicsRef.current.render = render;
    physicsRef.current.runner = Runner.create();
    physicsRef.current.initialized = true;

    Runner.run(physicsRef.current.runner, engine);
    Render.run(render);

    return () => {
      Runner.stop(physicsRef.current.runner);
      Render.stop(render);
      World.clear(engine.world);
      Engine.clear(engine);
      render.canvas.remove();
      physicsRef.current.initialized = false;
    };
  }, [removedBooks]); // Add removedBooks as dependency

  const handleBookClick = React.useCallback((bookId) => {
    if (!physicsRef.current.initialized) return;
    
    if (selectedBook === bookId) {
      setShowContent(false);
      setTimeout(() => setSelectedBook(null), 500);
      return;
    }

    const selectedBody = physicsRef.current.books[bookId];
    if (!selectedBody) return;

    Matter.Body.setVelocity(selectedBody, { x: 0, y: -15 });
    
    setTimeout(() => {
      Matter.World.remove(physicsRef.current.engine.world, selectedBody);
      delete physicsRef.current.books[bookId];
      setRemovedBooks(prev => new Set([...prev, bookId])); // Add to removed books
      setSelectedBook(bookId);
      setShowContent(true);
    }, 300);
  }, [selectedBook]);

  // Rest of your component remains the same...
  
  return (
    <div className="w-full bg-blue-600 p-2 overflow-hidden">
      <div className="max-w-4xl mx-auto">
        <div ref={sceneRef} className="relative h-96" style={{ touchAction: 'none' }} />
        
        {/* Book labels */}
        <div className="absolute inset-0" style={{ pointerEvents: 'none' }}>
          {books.map((book) => {
            if (removedBooks.has(book.id)) return null;
            const body = physicsRef.current.books[book.id];
            if (!body) return null;
            
            const { position, angle } = body;
            return (
              <div
                key={book.id}
                className="absolute"
                style={{
                  left: `${position.x}px`,
                  top: `${position.y}px`,
                  transform: `translate(-50%, -50%) rotate(${angle}rad)`,
                  width: `${book.width}px`,
                  height: `${book.height}px`,
                }}
              >
                <span className="absolute rotate-90 origin-left whitespace-nowrap font-mono tracking-widest text-xs left-4 text-blue-600 font-bold select-none">
                  {book.title}
                </span>
              </div>
            );
          })}
        </div>

        {/* Book content modal remains the same */}
        
      </div>
    </div>
  );
};

export default PhysicsBookshelf;
