import React, { useEffect, useState } from 'react';

const Curriculum = () => {
  const [employments, setEmployments] = useState([]);
  const [educations, setEducations] = useState([]);
  const [qualifications, setQualifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrcidData = async () => {
      try {
        const orcidId = '0000-0001-5931-5187';
        const apiUrl = `https://pub.orcid.org/v3.0/${orcidId}`;
        const response = await fetch(apiUrl, {
          headers: {
            Accept: 'application/json',
          },
        });
        
        if (!response.ok) {
          throw new Error(`Failed to fetch ORCID data: ${response.status}`);
        }
        
        const data = await response.json();

        setEmployments(data['activities-summary']?.employments?.['affiliation-group'] || []);
        setEducations(data['activities-summary']?.educations?.['affiliation-group'] || []);
        setQualifications(data['activities-summary']?.qualifications?.['affiliation-group'] || []);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchOrcidData();
  }, []);

  const formatDate = (date) => {
    if (!date || !date.year) return '';
    const { year, month, day } = date;
    return `${year}${month ? `-${String(month).padStart(2, '0')}` : ''}${day ? `-${String(day).padStart(2, '0')}` : ''}`;
  };

  if (loading) {
    return <div className="flex justify-center items-center h-64">
      <p className="text-amber-800">Loading curriculum data...</p>
    </div>;
  }

  if (error) {
    return <div className="flex justify-center items-center h-64">
      <p className="text-red-600">Error loading data: {error}</p>
    </div>;
  }

  return (
    <div className="book-2 p-8 rounded-lg border-2 space-y-12 max-w-4xl mx-auto">
      <div className="text-center mb-8">
        <span className="inline-block">Curriculum</span>
      </div>
      
      <div>
        <h2 className="text-3xl font-bold mb-6 text-amber-800 border-b-2 border-amber-500 pb-2">
          Employment
        </h2>
        {employments.length > 0 ? (
          <div className="space-y-8">
            {employments.map((employment, index) => {
              const summary = employment['summaries'][0]['employment-summary'];
              return (
                <div key={index} className="bg-amber-50 p-6 rounded-md shadow-md border-l-4 border-amber-500">
                  <h3 className="text-xl font-semibold text-amber-900 mb-2">
                    {summary['organization']['name']}
                  </h3>
                  {summary['department-name'] && (
                    <p className="text-amber-800 mb-1 font-medium">
                      {summary['department-name']}
                    </p>
                  )}
                  {summary['role-title'] && (
                    <p className="text-amber-700 mb-3 italic">
                      {summary['role-title']}
                    </p>
                  )}
                  <p className="text-amber-600 text-sm">
                    {formatDate(summary['start-date'])} — {formatDate(summary['end-date']) || 'Present'}
                  </p>
                </div>
              );
            })}
          </div>
        ) : (
          <p className="text-amber-600 italic">No employment history available</p>
        )}
      </div>
      
      <div>
        <h2 className="text-3xl font-bold mb-6 text-amber-800 border-b-2 border-amber-500 pb-2">
          Education
        </h2>
        {educations.length > 0 ? (
          <div className="space-y-8">
            {educations.map((education, index) => {
              const summary = education['summaries'][0]['education-summary'];
              return (
                <div key={index} className="bg-amber-50 p-6 rounded-md shadow-md border-l-4 border-amber-500">
                  <h3 className="text-xl font-semibold text-amber-900 mb-2">
                    {summary['organization']['name']}
                  </h3>
                  {summary['department-name'] && (
                    <p className="text-amber-800 mb-1 font-medium">
                      {summary['department-name']}
                    </p>
                  )}
                  {summary['role-title'] && (
                    <p className="text-amber-700 mb-3 italic">
                      {summary['role-title']}
                    </p>
                  )}
                  <p className="text-amber-600 text-sm">
                    {formatDate(summary['start-date'])} — {formatDate(summary['end-date']) || 'Present'}
                  </p>
                </div>
              );
            })}
          </div>
        ) : (
          <p className="text-amber-600 italic">No education history available</p>
        )}
      </div>
    
    </div>
  );
};

export default Curriculum;
