import React from 'react';
import SimplifiedBookShelf from './SimplifiedBookShelf';

const PersonalProfile = () => {
  const tags = ['digital humanities', 'semantic web', 'information science', 'knowledge engineering', 'GLAM'];

  return (
    <div className="relative h-screen w-full">
      
      <div className="absolute left-8 top-1/2 -translate-y-1/2 max-w-xl py-12">
        <h2 className="text-base md:text-lg mb-2">
          Hi, I am
        </h2>
        
        <h1 className="text-7xl tracking-wider">
          VALENTINA
        </h1>
        <h1 className="text-7xl tracking-wider mb-4">
          PASQUAL
        </h1>
        
        <h2 className="text-3xl md:text-4xl lg:text-5xl mb-6 font-semibold tracking-wider text-end">
          DIGITAL HUMANIST
        </h2>
        
        <p className="mb-6 text-sm text-justify leading-relaxed">
          PhD student at University of Bologna, mainly interested in Semantic Web technologies (LOD) applied to GLAM domain.
        </p>
        
        <div className="flex flex-wrap gap-2 justify-start">
          {tags.map((tag) => (
            <span 
              key={tag}
              className="text-xs font-medium px-2.5 py-0.5 rounded bg-gray-200 text-gray-800"
            >
              {tag}
            </span>
          ))}
        </div>
      </div>

      <div className="">
        <SimplifiedBookShelf />
      </div>

      <div className="absolute bottom-0 left-0 w-full h-2 bg-black"></div>
    </div>
  );
};

export default PersonalProfile;
