import React from 'react';

const Contacts = () => {
  const socialLinks = [
    { name: 'GitHub', url: 'https://github.com/ValentinaPasqual', icon: 'bi bi-github' },
    { name: 'Twitter', url: 'https://twitter.com/valepasqual', icon: 'bi bi-twitter' },
    { name: 'LinkedIn', url: 'https://www.linkedin.com/in/valentina-pasqual-96791017b/', icon: 'bi bi-linkedin' },
    { name: 'Google Scholar', url: 'https://scholar.google.com/citations?user=EVUFJkAAAAAJ&hl', icon: 'ai ai-google-scholar' },
    { name: 'Figshare', url: 'https://figshare.com/authors/Valentina_Pasqual/10212998', icon: 'ai ai-figshare' },
    { name: 'ORCID', url: 'https://orcid.org/0000-0001-5931-5187', icon: 'ai ai-orcid' }
  ];

  return (
    <div className="book-4 p-8 rounded-lg shadow-lg max-w-4xl mx-auto my-8">
      <div className="text-center mb-6">
        <span className="block mb-4">CONTACTS</span>
        <p className="text-blue-800 font-medium">Connect with Valentina Pasqual</p>
      </div>
      
      <div className="grid grid-cols-2 md:grid-cols-3 gap-6 mt-8">
        {socialLinks.map((link, index) => (
          <a 
            key={index}
            href={link.url}
            target="_blank"
            rel="noopener noreferrer"
            className="flex flex-col items-center justify-center p-4 bg-white bg-opacity-80 rounded-lg shadow-md hover:shadow-lg transition-all duration-300 hover:translate-y-1 hover:bg-blue-50 border-2 border-blue-200"
          >
            <div className="text-blue-700 mb-2">
              <i className={`${link.icon} text-3xl`} aria-hidden="true"></i>
            </div>
            <span className="text-blue-900 font-medium text-sm">{link.name}</span>
          </a>
        ))}
      </div>
      
      <div className="mt-10 text-center">
        <p className="text-blue-800 text-sm">
          Â© {new Date().getFullYear()} Valentina Pasqual
        </p>
      </div>
    </div>
  );
};

export default Contacts;
