import React, { useState } from 'react';
import Projects from './Projects';
import Publications from './Publications';
import Curriculum from './Curriculum';

const SimplifiedBookShelf = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [firstBookRotated, setFirstBookRotated] = useState(true);

  const books = [
    { 
      id: 1, 
      title: "Projects", 
      Component: "Projects",
      year: 2023,
      description: "A selection of project I worked",
      pages: 180,
      content: "A celebration of contemporary graphic design and festival aesthetics in modern culture.",
      accentColor: "color-combo-green",
      bookClass: "book-1",
      font:"poppins",
      isOblique: true,
      icon: "&#9737;"
    },
    { 
      id: 2, 
      title: "Curriculum", 
      Component: "Projects",
      author: "John Doe",
      year: 2024,
      description: "A comprehensive exploration of design fundamentals and their practical applications in modern contexts.",
      tags: ["Design", "Education", "Theory"],
      pages: 245,
      content: "Exploring the fundamental elements of design through geometric principles and visual harmony.",
      accentColor: "color-combo-yellow",
      bookClass: "book-2",
      font:"westgate",
      isOblique: false
    },
    { 
      id: 3, 
      title: "Publications", 
      Component: "Projects",
      author: "Robert Johnson",
      year: 2024,
      description: "An anthology of modern publishing works examining the intersection of traditional and digital mediums.",
      tags: ["Publishing", "Media", "Digital"],
      pages: 320,
      content: "The art of pencil and charcoal, examining traditional drawing techniques in the digital age.",
      accentColor: "color-combo-red",
      bookClass: "book-3",
      font:"boy",
      isOblique: false
    },
    { 
      id: 4, 
      title: "Contacts", 
      Component: "Projects",
      author: "Sarah Williams",
      year: 2023,
      description: "A comprehensive directory of industry professionals and creative practitioners, featuring insights and connections.",
      tags: ["Network", "Industry", "Contacts"],
      pages: 150,
      content: "Contemporary interpretations of traditional folk art patterns and methodologies.",
      accentColor: "color-combo-blue",
      font:"poppins",
      bookClass: "book-4",
      isOblique: false
    }
  ];

  const handleBookClick = (bookId) => {
    if (bookId === 1) {
      if (selectedBook !== 1) {
        setFirstBookRotated(false);
        setTimeout(() => {
          setSelectedBook(1);
        }, 100);
      } else {
        setSelectedBook(null);
        setTimeout(() => {
          setFirstBookRotated(true);
        }, 300);
      }
    } else {
      setSelectedBook(selectedBook === bookId ? null : bookId);
    }
  };

  return (
    <div className="h-screen w-screen flex relative overflow-hidden">
      {/* Main container - shifted right when book is open */}
      <div 
        className={`h-screen w-full flex items-end justify-end transition-all duration-500 ease-in-out`}
      >
        <div className={`h-[96vh] my-2 flex transition-all duration-500 ease-in-out`}>
          {books.map((book) => (
            <div
              key={book.id}
              className={`flex relative transition-all duration-500 ease-in-out
                        ${selectedBook && selectedBook !== book.id ? 'ml-2' : 'ml-0'}`}
            >
              <div className="flex">
                <div
                  onClick={() => handleBookClick(book.id)}
                  className={`${book.bookClass} book cursor-pointer flex flex-col items-center justify-center 
                            transition-all duration-300 ease-in-out
                            ${book.id === 1 ? (book.isOblique && firstBookRotated ? '-skew-x-3' : '') : (book.isOblique ? '-skew-x-12' : '')}
                            ${selectedBook && selectedBook !== book.id ? 'opacity-70 hover:opacity-100' : 'opacity-100'}
                            w-12 z-20 origin-bottom relative`}
                > 
                  <span className={`transform whitespace-nowrap text-lg tracking-wide transition-transform duration-300 ease-in-out -rotate-90
                                  ${book.id === 1 ? 
                                    (firstBookRotated && book.isOblique ? '-skew-x-12' : '') : 
                                    (book.isOblique ? '-skew-x-12' : '' )}`}>
                    <span dangerouslySetInnerHTML={{ __html: book.icon }} />
                    {book.title}
                    <span dangerouslySetInnerHTML={{ __html: book.icon }} />
                  </span>
                  
                  {/* Book description - horizontal text */}
                  <div className="absolute bottom-0 left-0 right-0">
                    <p className="text-[6px] text-left opacity-50 truncate px-1">
                      {book.description}
                    </p>
                  </div>
                </div>
                
                {/* Open book content panel */}
                {selectedBook === book.id && (
                  <div 
                    className={`absolute right-12 top-0 h-[96vh] w-[80vw] overflow-hidden overflow-y-auto 
                              bg-white shadow-2xl rounded-l-lg z-10 transition-all duration-500 ease-in-out`}
                  >
                    <div className="p-6 h-full">
                      <div className="flex justify-between items-center mb-6">
                        <h2 className="text-2xl font-bold">{book.title}</h2>
                        <button 
                          onClick={() => handleBookClick(book.id)}
                          className="text-gray-500 hover:text-gray-800"
                        >
                          &times; Close
                        </button>
                      </div>
                      
                      <div className="content-container">
                        {book.title === "Projects" && <Projects />}
                        {book.title === "Publications" && <Publications />}
                        {book.title === "Curriculum" && <Curriculum />}
                        {book.title === "Contacts" && <div>Contact content here</div>}
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SimplifiedBookShelf;
