import React, { useRef, useEffect, useState } from 'react';
import Bookshelf from './BookShelf';

const PersonalProfile = () => {
  const leftKeywords = ['digital humanities', 'semantic web', 'information science'];
  const rightKeywords = ['knowledge engineering', 'GLAM'];
  const contentRef = useRef(null);
  const [contentHeight, setContentHeight] = useState(0);

  useEffect(() => {
    if (contentRef.current) {
      const resizeObserver = new ResizeObserver((entries) => {
        for (let entry of entries) {
          setContentHeight(entry.contentRect.height);
        }
      });

      resizeObserver.observe(contentRef.current);
      return () => resizeObserver.disconnect();
    }
  }, []);

  return (
    <div className="min-h-screen flex items-center justify-center p-4 sm:p-6 md:p-8">
      <div className="w-full max-w-6xl h-auto md:h-[70vh] flex flex-col">
        {/* Main Content Wrapper */}
        <div className="flex flex-col md:flex-row flex-1">
          {/* Keywords Columns */}
          <div className="hidden md:flex w-48">
            {/* First column of keywords */}
            <div className="w-10 flex items-center">
              <div className="relative" style={{ height: contentHeight }}>
                {leftKeywords.map((keyword, index) => (
                  <div 
                    key={keyword}
                    className="absolute transform -rotate-90 origin-left"
                    style={{
                      left: '1rem',
                      bottom: `${index * (contentHeight / (leftKeywords.length - 1))}px`
                    }}
                  >
                    <span className="text-xs font-medium px-2.5 py-0.5 rounded bg-gray-200 text-gray-800 inline-block whitespace-nowrap">
                      {keyword}
                    </span>
                  </div>
                ))}
              </div>
            </div>
            {/* Second column of keywords */}
            <div className="w-10 flex items-center">
              <div className="relative" style={{ height: contentHeight }}>
                {rightKeywords.map((keyword, index) => (
                  <div 
                    key={keyword}
                    className="absolute transform -rotate-90 origin-left"
                    style={{
                      left: '1rem',
                      bottom: `${index * (contentHeight / Math.max(rightKeywords.length - 1, 1))}px`
                    }}
                  >
                    <span className="text-xs font-medium px-2.5 py-0.5 rounded bg-gray-200 text-gray-800 inline-block whitespace-nowrap">
                      {keyword}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Left Column - Personal Info */}
          <div className="w-full md:w-3/5 flex items-center justify-center relative mb-8 md:mb-0">
            <div ref={contentRef} className="max-w-md px-4 text-center md:text-left">
              <h1 className="text-lg md:text-xl mb-2">Hi, I am</h1> 
              <h1 className="text-7xl mb-4 font-bold">
                VALENTINA PASQUAL
              </h1>
              <h1 className="text-6xl mb-4 md:text-end font-semibold">
                DIGITAL HUMANIST
              </h1>
              <p className="mb-4 text-sm md:text-base">
                PhD student at University of Bologna, mainly interested in Semantic Web technologies (LOD) applied to GLAM domain.
              </p>
            </div>
          </div>

          {/* Right Column - Bookshelf */}
          <div className="w-full md:w-2/5 flex flex-col items-center justify-center relative mb-8 md:mb-0">
            <div className="flex-1 hidden md:block"></div>
            <Bookshelf />
          </div>
        </div>

        {/* Decorative Lines Container */}
        <div className="w-full h-1 bg-black mt-6 md:mt-0"></div>
      </div>
    </div>
  );
};

export default PersonalProfile;
