import React, { useState } from 'react';
import Projects from './Projects';
import Publications from './Publications';
import Curriculum from './Curriculum';

const EnhancedBookShelf = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [firstBookRotated, setFirstBookRotated] = useState(true);

  const books = [
    { 
      id: 1, 
      title: "Projects", 
      subtitle: "Creative Portfolio",
      Component: Projects,
      author: "Jane Smith",
      year: 2023,
      description: "A collection of innovative design projects showcasing contemporary approaches to visual communication and digital media.",
      tags: ["Portfolio", "Design", "Digital"],
      pages: 180,
      content: "A celebration of contemporary graphic design and festival aesthetics in modern culture.",
      accentColor: "color-combo-green",
      bookClass: "book-1",
      titleFont: "poppins",
      subtitleFont: "serif",
      isOblique: true,
      icon: "&#9737;",
      iconPosition: "both" // can be "start", "end", or "both"
    },
    { 
      id: 2, 
      title: "Curriculum", 
      subtitle: "Design Education",
      Component: Curriculum,
      author: "John Doe",
      year: 2024,
      description: "A comprehensive exploration of design fundamentals and their practical applications in modern contexts.",
      tags: ["Design", "Education", "Theory"],
      pages: 245,
      content: "Exploring the fundamental elements of design through geometric principles and visual harmony.",
      accentColor: "color-combo-yellow",
      bookClass: "book-2",
      titleFont: "westgate",
      subtitleFont: "poppins",
      isOblique: false,
      icon: "&#9733;",
      iconPosition: "start"
    },
    { 
      id: 3, 
      title: "Publications", 
      subtitle: "Written Works",
      Component: Publications,
      author: "Robert Johnson",
      year: 2024,
      description: "An anthology of modern publishing works examining the intersection of traditional and digital mediums.",
      tags: ["Publishing", "Media", "Digital"],
      pages: 320,
      content: "The art of pencil and charcoal, examining traditional drawing techniques in the digital age.",
      accentColor: "color-combo-red",
      bookClass: "book-3",
      titleFont: "boy",
      subtitleFont: "helvetica",
      isOblique: false,
      icon: "&#128214;",
      iconPosition: "end"
    },
    { 
      id: 4, 
      title: "Contacts", 
      subtitle: "Connect & Network",
      Component: Contacts,
      author: "Sarah Williams",
      year: 2023,
      description: "A comprehensive directory of industry professionals and creative practitioners, featuring insights and connections.",
      tags: ["Network", "Industry", "Contacts"],
      pages: 150,
      content: "Contemporary interpretations of traditional folk art patterns and methodologies.",
      accentColor: "color-combo-blue",
      titleFont: "poppins",
      subtitleFont: "montserrat",
      bookClass: "book-4",
      isOblique: false,
      icon: "&#128232;",
      iconPosition: "none"
    }
  ];

  const handleBookClick = (bookId) => {
    if (bookId === 1) {
      if (selectedBook !== 1) {
        setFirstBookRotated(false);
        setTimeout(() => {
          setSelectedBook(1);
        }, 100);
      } else {
        setSelectedBook(null);
        setTimeout(() => {
          setFirstBookRotated(true);
        }, 300);
      }
    } else {
      setSelectedBook(selectedBook === bookId ? null : bookId);
    }
  };

  // Helper function to render icon based on position preference
  const renderIcon = (book, position) => {
    if (!book.icon || book.iconPosition === "none") return null;
    if (book.iconPosition === position || book.iconPosition === "both") {
      return <span className="book-icon" dangerouslySetInnerHTML={{ __html: book.icon }} />;
    }
    return null;
  };

  return (
    <div className="h-screen flex items-end justify-end">
      <div className="h-[96vh] my-2 flex">
        {books.map((book) => (
          <div
            key={book.id}
            className="flex relative"
          >
            <div className="flex">
              <div
                onClick={() => handleBookClick(book.id)}
                className={`${book.bookClass} book cursor-pointer flex items-center justify-center transition-transform duration-300 ease-in-out
                          ${book.id === 1 ? (book.isOblique && firstBookRotated ? '-skew-x-3' : '') : (book.isOblique ? '-skew-x-12' : '')}
                          w-12 z-10 origin-bottom`}
              > 
                <div className={`transform flex flex-col items-center whitespace-nowrap transition-transform duration-300 ease-in-out -rotate-90
                              ${book.id === 1 ? 
                                (firstBookRotated && book.isOblique ? '-skew-x-12' : '') : 
                                (book.isOblique ? '-skew-x-12' : '' )}`}>
                  
                  {/* Book title with custom font and icons */}
                  <div className="book-title-container flex items-center">
                    {renderIcon(book, "start")}
                    <span className={`text-lg tracking-wide font-${book.titleFont}`}>
                      {book.title}
                    </span>
                    {renderIcon(book, "end")}
                  </div>
                  
                  {/* Optional subtitle with custom font */}
                  {book.subtitle && (
                    <span className={`text-xs font-${book.subtitleFont} opacity-75 mt-1`}>
                      {book.subtitle}
                    </span>
                  )}
                </div>
              </div>
              
              <div 
                className={`overflow-hidden overflow-y-auto transition-all duration-300 ease-in-out
                            ${selectedBook === book.id ? 'w-96 opacity-100' : 'w-0 opacity-0'}`}
              >
                {selectedBook === book.id && <book.Component />}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EnhancedBookShelf;
